    ERC2981Upgradeable
{
    using StringsUpgradeable for uint256;

    /// @dev Structure for pre-mint data
    struct PreMintData {
        // address to mint NFTs to
        address sender;
        // No. of NFTs to mint
        uint96 quantity;
    }

    /// @dev Structure for a set of pre-mint data.
    struct PreMintDataSet {
        // pre-mint data array
        PreMintData[] preMintDataArr;
        // maps a user address to the index of the user's pre-mint data in the
        // `preMintDataArr` array. Plus 1 because index 0 means data does not
        // exist for that user.
        mapping(address => uint256) indexes;
    }

    /// @notice Role granted to project owners
    bytes32 public constant override PROJECT_OWNER_ROLE =
        keccak256("PROJECT_OWNER_ROLE");

    /// @notice Percentage base point
    uint256 public constant BASIS_POINT_PRECISION = 10_000;

    /// @notice The collection size (e.g 10000)
    uint256 public override collectionSize;

    /// @notice Amount of NFTs reserved for the project owner (e.g 200)
    /// @dev It can be minted any time via `devMint`
    uint256 public override amountForDevs;

    /// @notice Amount of NFTs available for the allowlist mint (e.g 1000)
    uint256 public override amountForAllowlist;

    /// @notice Max amount of NFTs an address can mint in public phases
    uint256 public override maxPerAddressDuringMint;

    /// @notice The fees collected by Joepegs on the sale benefits
    /// @dev In basis points e.g 100 for 1%
    uint256 public override joeFeePercent;

    /// @notice The address to which the fees on the sale will be sent
    address public override joeFeeCollector;

    /// @notice Batch reveal contract
    IBatchReveal public batchReveal;

    /// @notice Token URI after collection reveal
    string public override baseURI;

    /// @notice Token URI before the collection reveal
    string public override unrevealedURI;

    /// @notice The amount of NFTs each allowed address can mint during
    /// the pre-mint or allowlist mint
    mapping(address => uint256) public override allowlist;

    /// @notice Tracks the amount of NFTs minted by `projectOwner`
    uint256 public override amountMintedByDevs;

    /// @notice Tracks the amount of NFTs minted in the Pre-Mint phase
    uint256 public override amountMintedDuringPreMint;

    /// @notice Tracks the amount of pre-minted NFTs that have been claimed
    uint256 public override amountClaimedDuringPreMint;

    /// @notice Tracks the amount of NFTs minted on Allowlist phase
    uint256 public override amountMintedDuringAllowlist;

    /// @notice Tracks the amount of NFTs minted on Public Sale phase
    uint256 public override amountMintedDuringPublicSale;

    /// @notice Start time of the pre-mint in seconds
    uint256 public override preMintStartTime;

    /// @notice Start time of the allowlist mint in seconds
    uint256 public override allowlistStartTime;

    /// @notice Start time of the public sale in seconds
    /// @dev A timestamp greater than the allowlist mint start
    uint256 public override publicSaleStartTime;

    /// @notice End time of the public sale in seconds
    /// @dev A timestamp greater than the public sale start
    uint256 public override publicSaleEndTime;

    /// @notice Start time when funds can be withdrawn
    uint256 public override withdrawAVAXStartTime;

    /// @notice Contract filtering allowed operators, preventing unauthorized contract to transfer NFTs
    /// By default, Launchpeg contracts are subscribed to OpenSea's Curated Subscription Address at 0x3cc6CddA760b79bAfa08dF41ECFA224f810dCeB6
    IOperatorFilterRegistry public operatorFilterRegistry;

    /// @dev Set of pending pre-mint data (user address and quantity)
    PreMintDataSet private _pendingPreMints;

    /// @dev Emitted on initializeJoeFee()
    /// @param feePercent The fees collected by Joepegs on the sale benefits
    /// @param feeCollector The address to which the fees on the sale will be sent
    event JoeFeeInitialized(uint256 feePercent, address feeCollector);

    /// @dev Emitted on devMint()
    /// @param sender The address that minted
    /// @param quantity Amount of NFTs minted
    event DevMint(address indexed sender, uint256 quantity);

    /// @dev Emitted on preMint()
    /// @param sender The address that minted
    /// @param quantity Amount of NFTs minted
    /// @param price Price of 1 NFT
    event PreMint(address indexed sender, uint256 quantity, uint256 price);

    /// @dev Emitted on auctionMint(), claimPreMint(), batchClaimPreMint(),
    /// allowlistMint(), publicSaleMint()
    /// @param sender The address that minted
    /// @param quantity Amount of NFTs minted
    /// @param price Price in AVAX for the NFTs
    /// @param startTokenId The token ID of the first minted NFT:
    /// if `startTokenId` = 100 and `quantity` = 2, `sender` minted 100 and 101
    /// @param phase The phase in which the mint occurs
    event Mint(
        address indexed sender,
        uint256 quantity,
        uint256 price,
        uint256 startTokenId,
        Phase phase
    );

    /// @dev Emitted on withdrawAVAX()
    /// @param sender The address that withdrew the tokens
    /// @param amount Amount of AVAX transfered to `sender`
    /// @param fee Amount of AVAX paid to the fee collector
    event AvaxWithdraw(address indexed sender, uint256 amount, uint256 fee);

    /// @dev Emitted on setBaseURI()
    /// @param baseURI The new base URI
    event BaseURISet(string baseURI);

    /// @dev Emitted on setUnrevealedURI()
    /// @param unrevealedURI The new base URI
    event UnrevealedURISet(string unrevealedURI);

    /// @dev Emitted on seedAllowlist()
    event AllowlistSeeded();

    /// @dev Emitted on _setDefaultRoyalty()
    /// @param receiver Royalty fee collector
    /// @param feePercent Royalty fee percent in basis point
    event DefaultRoyaltySet(address indexed receiver, uint256 feePercent);

    /// @dev Emitted on setPreMintStartTime()
    /// @param preMintStartTime New pre-mint start time
    event PreMintStartTimeSet(uint256 preMintStartTime);

    /// @dev Emitted on setAllowlistStartTime()
    /// @param allowlistStartTime New allowlist start time
    event AllowlistStartTimeSet(uint256 allowlistStartTime);

    /// @dev Emitted on setPublicSaleStartTime()
    /// @param publicSaleStartTime New public sale start time
    event PublicSaleStartTimeSet(uint256 publicSaleStartTime);

    /// @dev Emitted on setPublicSaleEndTime()
    /// @param publicSaleEndTime New public sale end time
    event PublicSaleEndTimeSet(uint256 publicSaleEndTime);

    /// @dev Emitted on setWithdrawAVAXStartTime()
    /// @param withdrawAVAXStartTime New withdraw AVAX start time
    event WithdrawAVAXStartTimeSet(uint256 withdrawAVAXStartTime);

    /// @dev Emitted on updateOperatorFilterRegistryAddress()
    /// @param operatorFilterRegistry New operator filter registry
    event OperatorFilterRegistryUpdated(
        IOperatorFilterRegistry indexed operatorFilterRegistry
    );

    modifier isEOA() {
        if (tx.origin != msg.sender) {
            revert Launchpeg__Unauthorized();
        }
        _;
    }

    /// @notice Checks if the current phase matches the required phase
    modifier atPhase(Phase _phase) {
        if (currentPhase() != _phase) {
            revert Launchpeg__WrongPhase();
        }
        _;
    }

    /// @notice Pre-mints can be claimed from the allowlist phase
    /// (including after sale ends)
    modifier isClaimPreMintAvailable() {
        if (block.timestamp < allowlistStartTime) {
            revert Launchpeg__WrongPhase();
        }
        _;
    }

    /// @notice Phase time can be updated if it has been initialized and
    // the time has not passed
    modifier isTimeUpdateAllowed(uint256 _phaseTime) {
        if (_phaseTime == 0) {
            revert Launchpeg__NotInitialized();
        }
        if (_phaseTime <= block.timestamp) {
            revert Launchpeg__WrongPhase();
        }
        _;
    }

    /// @notice Checks if new time is equal to or after block timestamp
    modifier isNotBeforeBlockTimestamp(uint256 _newTime) {
        if (_newTime < block.timestamp) {
            revert Launchpeg__InvalidPhases();
        }
        _;
    }

    /// @notice Allow spending tokens from addresses with balance
    /// Note that this still allows listings and marketplaces with escrow to transfer tokens if transferred
    /// from an EOA.
    modifier onlyAllowedOperator(address from) virtual {
        if (from != msg.sender) {
            _checkFilterOperator(msg.sender);
        }
        _;
    }

    /// @notice Allow approving tokens transfers
    modifier onlyAllowedOperatorApproval(address operator) virtual {
        _checkFilterOperator(operator);
        _;
    }

    /// @dev BaseLaunchpeg initialization
    /// @param _collectionData Launchpeg collection data
    /// @param _ownerData Launchpeg owner data
    function initializeBaseLaunchpeg(
        CollectionData calldata _collectionData,
        CollectionOwnerData calldata _ownerData
    ) internal onlyInitializing {
        __SafePausable_init();
        __ReentrancyGuard_init();
        __ERC2981_init();
        __ERC721A_init(_collectionData.name, _collectionData.symbol);

        if (_ownerData.owner == address(0)) {
            revert Launchpeg__InvalidOwner();
        }
        if (_ownerData.projectOwner == address(0)) {
            revert Launchpeg__InvalidProjectOwner();
        }
        if (
            _collectionData.collectionSize == 0 ||
            _collectionData.amountForDevs + _collectionData.amountForAllowlist >
            _collectionData.collectionSize
        ) {
            revert Launchpeg__LargerCollectionSizeNeeded();
        }
        if (
            _collectionData.maxPerAddressDuringMint >
            _collectionData.collectionSize
        ) {
            revert Launchpeg__InvalidMaxPerAddressDuringMint();
        }

        // Initialize the operator filter registry and subcribe to OpenSea's list
        IOperatorFilterRegistry _operatorFilterRegistry = IOperatorFilterRegistry(
                0x000000000000AAeB6D7670E522A718067333cd4E
            );
        if (address(_operatorFilterRegistry).code.length > 0) {
            _operatorFilterRegistry.registerAndSubscribe(
                address(this),
                0x3cc6CddA760b79bAfa08dF41ECFA224f810dCeB6
            );
        }
        _updateOperatorFilterRegistryAddress(_operatorFilterRegistry);

        // Default royalty is 5%
        _setDefaultRoyalty(_ownerData.royaltyReceiver, 500);
        _initializeJoeFee(_ownerData.joeFeePercent, _ownerData.joeFeeCollector);

        batchReveal = IBatchReveal(_collectionData.batchReveal);
        collectionSize = _collectionData.collectionSize;
        maxPerAddressDuringMint = _collectionData.maxPerAddressDuringMint;
        amountForDevs = _collectionData.amountForDevs;
        amountForAllowlist = _collectionData.amountForAllowlist;

        grantRole(PAUSER_ROLE, msg.sender);
        grantRole(PROJECT_OWNER_ROLE, _ownerData.projectOwner);
        _transferOwnership(_ownerData.owner);
    }

    /// @notice Initialize the sales fee percent taken by Joepegs and address that collects the fees
    /// @param _joeFeePercent The fees collected by Joepegs on the sale benefits
    /// @param _joeFeeCollector The address to which the fees on the sale will be sent
    function _initializeJoeFee(uint256 _joeFeePercent, address _joeFeeCollector)
        internal
    {
        if (_joeFeePercent > BASIS_POINT_PRECISION) {
            revert Launchpeg__InvalidPercent();
        }
        if (_joeFeeCollector == address(0)) {
            revert Launchpeg__InvalidJoeFeeCollector();
        }
        joeFeePercent = _joeFeePercent;
        joeFeeCollector = _joeFeeCollector;
        emit JoeFeeInitialized(_joeFeePercent, _joeFeeCollector);
    }

    /// @notice Initialize batch reveal. Leave undefined to disable
    /// batch reveal for the collection.
    /// @dev Can only be set once. Cannot be initialized once sale has ended.
    /// @param _batchReveal Batch reveal contract address
    function initializeBatchReveal(address _batchReveal)
        external
        override
        onlyOwner
    {
        if (address(batchReveal) != address(0)) {
            revert Launchpeg__BatchRevealAlreadyInitialized();
        }
        // Disable once sale has ended
        if (publicSaleEndTime > 0 && block.timestamp >= publicSaleEndTime) {
            revert Launchpeg__WrongPhase();
        }
        batchReveal = IBatchReveal(_batchReveal);
    }

    /// @notice Set the royalty fee
    /// @param _receiver Royalty fee collector
    /// @param _feePercent Royalty fee percent in basis point
    function setRoyaltyInfo(address _receiver, uint96 _feePercent)
        external
        override
        onlyOwner
    {
        // Royalty fees are limited to 25%
        if (_feePercent > 2_500) {
            revert Launchpeg__InvalidRoyaltyInfo();
        }
        _setDefaultRoyalty(_receiver, _feePercent);
        emit DefaultRoyaltySet(_receiver, _feePercent);
    }

    /// @notice Update the address that the contract will make OperatorFilter checks against. When set to the zero
    /// address, checks will be bypassed. OnlyOwner
    /// @param _newRegistry The address of the new OperatorFilterRegistry
    function updateOperatorFilterRegistryAddress(
        IOperatorFilterRegistry _newRegistry
    ) external onlyOwner {
        _updateOperatorFilterRegistryAddress(_newRegistry);
    }

    /// @notice Set amount of NFTs mintable per address during the allowlist phase
    /// @param _addresses List of addresses allowed to mint during the allowlist phase
    /// @param _numNfts List of NFT quantities mintable per address
    function seedAllowlist(
        address[] calldata _addresses,
        uint256[] calldata _numNfts
    ) external override onlyOwner {
        uint256 addressesLength = _addresses.length;
        if (addressesLength != _numNfts.length) {
            revert Launchpeg__WrongAddressesAndNumSlotsLength();
        }
        for (uint256 i; i < addressesLength; i++) {
            allowlist[_addresses[i]] = _numNfts[i];
        }

        emit AllowlistSeeded();
    }

    /// @notice Set the base URI
    /// @dev This sets the URI for revealed tokens
    /// Only callable by project owner
    /// @param _baseURI Base URI to be set
    function setBaseURI(string calldata _baseURI) external override onlyOwner {
        baseURI = _baseURI;
        emit BaseURISet(baseURI);
    }

    /// @notice Set the unrevealed URI
    /// @dev Only callable by project owner
    /// @param _unrevealedURI Unrevealed URI to be set
    function setUnrevealedURI(string calldata _unrevealedURI)
        external
        override
        onlyOwner
    {
        unrevealedURI = _unrevealedURI;
        emit UnrevealedURISet(unrevealedURI);
    }

    /// @notice Set the allowlist start time. Can only be set after phases
    /// have been initialized.
    /// @dev Only callable by owner
    /// @param _allowlistStartTime New allowlist start time
    function setAllowlistStartTime(uint256 _allowlistStartTime)
        external
        override
        onlyOwner
        isTimeUpdateAllowed(allowlistStartTime)
        isNotBeforeBlockTimestamp(_allowlistStartTime)
    {
        if (
            _allowlistStartTime < preMintStartTime ||
            publicSaleStartTime < _allowlistStartTime
        ) {
            revert Launchpeg__InvalidPhases();
        }
        allowlistStartTime = _allowlistStartTime;
        emit AllowlistStartTimeSet(_allowlistStartTime);
    }

    /// @notice Set the public sale start time. Can only be set after phases
    /// have been initialized.
    /// @dev Only callable by owner
    /// @param _publicSaleStartTime New public sale start time
    function setPublicSaleStartTime(uint256 _publicSaleStartTime)
        external
        override
        onlyOwner
        isTimeUpdateAllowed(publicSaleStartTime)
        isNotBeforeBlockTimestamp(_publicSaleStartTime)
    {
        if (
            _publicSaleStartTime < allowlistStartTime ||
            publicSaleEndTime < _publicSaleStartTime
        ) {
            revert Launchpeg__InvalidPhases();
        }

        publicSaleStartTime = _publicSaleStartTime;
        emit PublicSaleStartTimeSet(_publicSaleStartTime);
    }

    /// @notice Set the public sale end time. Can only be set after phases
    /// have been initialized.
    /// @dev Only callable by owner
    /// @param _publicSaleEndTime New public sale end time
    function setPublicSaleEndTime(uint256 _publicSaleEndTime)
        external
        override
        onlyOwner
        isTimeUpdateAllowed(publicSaleEndTime)
        isNotBeforeBlockTimestamp(_publicSaleEndTime)
    {
        if (_publicSaleEndTime < publicSaleStartTime) {
            revert Launchpeg__InvalidPhases();
        }
        publicSaleEndTime = _publicSaleEndTime;
        emit PublicSaleEndTimeSet(_publicSaleEndTime);
    }

    /// @notice Set the withdraw AVAX start time.
    /// @param _withdrawAVAXStartTime New public sale end time
    function setWithdrawAVAXStartTime(uint256 _withdrawAVAXStartTime)
        external
        override
        onlyOwner
        isNotBeforeBlockTimestamp(_withdrawAVAXStartTime)
    {
        withdrawAVAXStartTime = _withdrawAVAXStartTime;
        emit WithdrawAVAXStartTimeSet(_withdrawAVAXStartTime);
    }

    /// @notice The remaining no. of pre-minted NFTs for the user address
    /// @param _user user address
    function userPendingPreMints(address _user)
        public
        view
        override
        returns (uint256)
    {
        uint256 idx = _pendingPreMints.indexes[_user];
        if (idx == 0) {
            return 0;
        }
        return _pendingPreMints.preMintDataArr[idx - 1].quantity;
    }

    /// @notice Mint NFTs to the project owner
    /// @dev Can only mint up to `amountForDevs`
    /// @param _quantity Quantity of NFTs to mint
    function devMint(uint256 _quantity)
        external
        override
        onlyOwnerOrRole(PROJECT_OWNER_ROLE)
        whenNotPaused
    {
        if (_totalSupplyWithPreMint() + _quantity > collectionSize) {
            revert Launchpeg__MaxSupplyReached();
        }
        if (amountMintedByDevs + _quantity > amountForDevs) {
            revert Launchpeg__MaxSupplyForDevReached();
        }
        amountMintedByDevs = amountMintedByDevs + _quantity;
        _batchMint(msg.sender, _quantity, maxPerAddressDuringMint);
        emit DevMint(msg.sender, _quantity);
    }

    /// @notice Mint NFTs during the pre-mint
    /// @param _quantity Quantity of NFTs to mint
    function preMint(uint96 _quantity)
        external
        payable
        override
        whenNotPaused
        atPhase(Phase.PreMint)
    {
        if (_quantity == 0) {
            revert Launchpeg__InvalidQuantity();
        }
        if (_quantity > allowlist[msg.sender]) {
            revert Launchpeg__NotEligibleForAllowlistMint();
        }
        if (amountMintedDuringPreMint + _quantity > amountForAllowlist) {
            revert Launchpeg__MaxSupplyReached();
        }
        allowlist[msg.sender] -= _quantity;
        amountMintedDuringPreMint += _quantity;
        _addPreMint(msg.sender, _quantity);
        uint256 price = _getPreMintPrice();
        uint256 totalCost = price * _quantity;
        emit PreMint(msg.sender, _quantity, price);
        _refundIfOver(totalCost);
    }

    /// @notice Claim pre-minted NFTs
    function claimPreMint()
        external
        override
        whenNotPaused
        isClaimPreMintAvailable
    {
        uint256 quantity = userPendingPreMints(msg.sender);
        if (quantity == 0) {
            revert Launchpeg__InvalidClaim();
        }
        _removePreMint(msg.sender, uint96(quantity));
        amountClaimedDuringPreMint += quantity;
        uint256 price = _getPreMintPrice();
        _batchMint(msg.sender, quantity, maxPerAddressDuringMint);
        emit Mint(
            msg.sender,
            quantity,
            price,
            _totalMinted() - quantity,
            Phase.PreMint
        );
    }

    /// @notice Claim pre-minted NFTs for users
    /// @param _maxQuantity Max quantity of NFTs to mint
    function batchClaimPreMint(uint96 _maxQuantity)
        external
        override
        whenNotPaused
        isClaimPreMintAvailable
    {
        if (_maxQuantity == 0) {
            revert Launchpeg__InvalidQuantity();
        }
        if (amountMintedDuringPreMint == amountClaimedDuringPreMint) {
            revert Launchpeg__InvalidClaim();
        }
        uint256 maxBatchSize = maxPerAddressDuringMint;
        uint256 price = _getPreMintPrice();
        uint96 remQuantity = _maxQuantity;
        uint96 mintQuantity;
        for (
            uint256 len = _pendingPreMints.preMintDataArr.length;
            len > 0 && remQuantity > 0;

        ) {
            PreMintData memory preMintData = _pendingPreMints.preMintDataArr[
                len - 1
            ];
            if (preMintData.quantity > remQuantity) {
                mintQuantity = remQuantity;
            } else {
                mintQuantity = preMintData.quantity;
                --len;
            }
            _removePreMint(preMintData.sender, mintQuantity);
            remQuantity -= mintQuantity;
            _batchMint(preMintData.sender, mintQuantity, maxBatchSize);
            emit Mint(
                preMintData.sender,
                mintQuantity,
                price,
                _totalMinted() - mintQuantity,
                Phase.PreMint
            );
        }
        amountClaimedDuringPreMint += (_maxQuantity - remQuantity);
    }

    /// @notice Mint NFTs during the allowlist mint
    /// @param _quantity Quantity of NFTs to mint
    function allowlistMint(uint256 _quantity)
        external
        payable
        override
        whenNotPaused
        atPhase(Phase.Allowlist)
    {
        if (_quantity > allowlist[msg.sender]) {
            revert Launchpeg__NotEligibleForAllowlistMint();
        }
        if (
            amountMintedDuringPreMint +
                amountMintedDuringAllowlist +
                _quantity >
            amountForAllowlist
        ) {
            revert Launchpeg__MaxSupplyReached();
        }
        allowlist[msg.sender] -= _quantity;
        uint256 price = _getAllowlistPrice();
        uint256 totalCost = price * _quantity;

        _batchMint(msg.sender, _quantity, maxPerAddressDuringMint);
        amountMintedDuringAllowlist += _quantity;
        emit Mint(
            msg.sender,
            _quantity,
            price,
            _totalMinted() - _quantity,
            Phase.Allowlist
        );
        _refundIfOver(totalCost);
    }

    /// @notice Mint NFTs during the public sale
    /// @param _quantity Quantity of NFTs to mint
    function publicSaleMint(uint256 _quantity)
        external
        payable
        override
        isEOA
        whenNotPaused
        atPhase(Phase.PublicSale)
    {
        if (
            numberMintedWithPreMint(msg.sender) + _quantity >
            maxPerAddressDuringMint
        ) {
            revert Launchpeg__CanNotMintThisMany();
        }
        // ensure sufficient supply for devs. note we can skip this check
        // in prior phases as long as they do not exceed the phase allocation
        // and the total phase allocations do not exceed collection size
        uint256 remainingDevAmt = amountForDevs - amountMintedByDevs;
        if (
            _totalSupplyWithPreMint() + remainingDevAmt + _quantity >
            collectionSize
        ) {
            revert Launchpeg__MaxSupplyReached();
        }
        uint256 price = _getPublicSalePrice();
        uint256 total = price * _quantity;

        _mint(msg.sender, _quantity, "", false);
        amountMintedDuringPublicSale += _quantity;
        emit Mint(
            msg.sender,
            _quantity,
            price,
            _totalMinted() - _quantity,
            Phase.PublicSale
        );
        _refundIfOver(total);
    }

    /// @dev Returns pre-mint price. Used by mint methods.
    function _getPreMintPrice() internal view virtual returns (uint256);

    /// @dev Returns allowlist price. Used by mint methods.
    function _getAllowlistPrice() internal view virtual returns (uint256);

    /// @dev Returns public sale price. Used by mint methods.
    function _getPublicSalePrice() internal view virtual returns (uint256);

    /// @notice Withdraw AVAX to the given recipient
    /// @param _to Recipient of the earned AVAX
    function withdrawAVAX(address _to)
        external
        override
        onlyOwnerOrRole(PROJECT_OWNER_ROLE)
        nonReentrant
        whenNotPaused
    {
        if (
            withdrawAVAXStartTime > block.timestamp ||
            withdrawAVAXStartTime == 0
        ) {
            revert Launchpeg__WithdrawAVAXNotAvailable();
        }

        uint256 amount = address(this).balance;
        uint256 fee;
        bool sent;

        if (joeFeePercent > 0) {
            fee = (amount * joeFeePercent) / BASIS_POINT_PRECISION;
            amount = amount - fee;

            (sent, ) = joeFeeCollector.call{value: fee}("");
            if (!sent) {
                revert Launchpeg__TransferFailed();
            }
        }

        (sent, ) = _to.call{value: amount}("");
        if (!sent) {
            revert Launchpeg__TransferFailed();
        }

        emit AvaxWithdraw(_to, amount, fee);
    }

    /// @notice Returns the ownership data of a specific token ID
    /// @param _tokenId Token ID
    /// @return TokenOwnership Ownership struct for a specific token ID
    function getOwnershipData(uint256 _tokenId)
        external
        view
        override
        returns (TokenOwnership memory)
    {
        return _ownershipOf(_tokenId);
    }

    /// @notice Returns the Uniform Resource Identifier (URI) for `tokenId` token.
    /// @param _id Token id
    /// @return URI Token URI
    function tokenURI(uint256 _id)
        public
        view
        override(ERC721AUpgradeable, IERC721MetadataUpgradeable)
        returns (string memory)
    {
        if (address(batchReveal) == address(0)) {
            return string(abi.encodePacked(baseURI, _id.toString()));
        } else if (
            _id >= batchReveal.launchpegToLastTokenReveal(address(this))
        ) {
            return unrevealedURI;
        } else {
            return
                string(
                    abi.encodePacked(
                        baseURI,
                        batchReveal
                            .getShuffledTokenId(address(this), _id)
                            .toString()
                    )
                );
        }
    }

    /// @notice Returns the number of NFTs minted by a specific address
    /// @param _owner The owner of the NFTs
    /// @return numberMinted Number of NFTs minted
    function numberMinted(address _owner)
        public
        view
        override
        returns (uint256)
    {
        return _numberMinted(_owner);
    }

    /// @dev Returns true if this contract implements the interface defined by
    /// `interfaceId`. See the corresponding
    /// https://eips.ethereum.org/EIPS/eip-165#how-interfaces-are-identified[EIP section]
    /// to learn more about how these IDs are created.
    /// This function call must use less than 30 000 gas.
    /// @param _interfaceId InterfaceId to consider. Comes from type(InterfaceContract).interfaceId
    /// @return isInterfaceSupported True if the considered interface is supported
    function supportsInterface(bytes4 _interfaceId)
        public
        view
        virtual
        override(
            ERC721AUpgradeable,
            ERC2981Upgradeable,
            IERC165Upgradeable,
            SafePausableUpgradeable
        )
        returns (bool)
    {
        return
            _interfaceId == type(IBaseLaunchpeg).interfaceId ||
            ERC721AUpgradeable.supportsInterface(_interfaceId) ||
            ERC2981Upgradeable.supportsInterface(_interfaceId) ||
            ERC165Upgradeable.supportsInterface(_interfaceId) ||
            SafePausableUpgradeable.supportsInterface(_interfaceId) ||
            super.supportsInterface(_interfaceId);
    }

    /// @dev Verifies that enough AVAX has been sent by the sender and refunds the extra tokens if any
    /// @param _price The price paid by the sender for minting NFTs
    function _refundIfOver(uint256 _price) internal {
        if (msg.value < _price) {
            revert Launchpeg__NotEnoughAVAX(msg.value);
        }
        if (msg.value > _price) {
            (bool success, ) = msg.sender.call{value: msg.value - _price}("");
            if (!success) {
                revert Launchpeg__TransferFailed();
            }
        }
    }

    /// @notice Returns the current phase
    /// @return phase Current phase
    function currentPhase() public view virtual override returns (Phase);

    /// @notice Reveals the next batch if the reveal conditions are met
    function revealNextBatch() external override isEOA whenNotPaused {
        if (address(batchReveal) == address(0)) {
            revert Launchpeg__BatchRevealDisabled();
        }
        if (!batchReveal.revealNextBatch(address(this), totalSupply())) {
            revert Launchpeg__RevealNextBatchNotAvailable();
        }
    }

    /// @notice Tells you if a batch can be revealed
    /// @return bool Whether reveal can be triggered or not
    /// @return uint256 The number of the next batch that will be revealed
    function hasBatchToReveal() external view override returns (bool, uint256) {
        if (address(batchReveal) == address(0)) {
            return (false, 0);
        }
        return batchReveal.hasBatchToReveal(address(this), totalSupply());
    }

    /// @dev Total supply including pre-mints
    function _totalSupplyWithPreMint() internal view returns (uint256) {
        return
            totalSupply() +
            amountMintedDuringPreMint -
            amountClaimedDuringPreMint;
    }

    /// @notice Number minted by user including pre-mints
    function numberMintedWithPreMint(address _owner)
        public
        view
        override
        returns (uint256)
    {
        return _numberMinted(_owner) + userPendingPreMints(_owner);
    }

    /// @dev Adds pre-mint data to the pre-mint data set
    /// @param _sender address to mint NFTs to
    /// @param _quantity No. of NFTs to add to mint quantity
    function _addPreMint(address _sender, uint96 _quantity) private {
        PreMintDataSet storage set = _pendingPreMints;
        uint256 idx = set.indexes[_sender];
        // user exists in set
        if (idx != 0) {
            set.preMintDataArr[idx - 1].quantity += _quantity;
        } else {
            PreMintData memory preMintData = PreMintData({
                sender: _sender,
                quantity: _quantity
            });
            set.preMintDataArr.push(preMintData);
            set.indexes[_sender] = set.preMintDataArr.length;
        }
    }

    /// @dev Removes pre-mint data from the pre-mint data set
    /// @param _sender address to mint NFTs to
    /// @param _quantity No. of NFTs to remove from mint quantity
    function _removePreMint(address _sender, uint96 _quantity) private {
        PreMintDataSet storage set = _pendingPreMints;
        uint256 idx = set.indexes[_sender];
        // user exists in set
        if (idx != 0) {
            uint96 currQuantity = set.preMintDataArr[idx - 1].quantity;
            uint96 newQuantity = (currQuantity > _quantity)
                ? currQuantity - _quantity
                : 0;
            // remove from set
            if (newQuantity == 0) {
                uint256 toDeleteIdx = idx - 1;
                uint256 lastIdx = set.preMintDataArr.length - 1;
                if (toDeleteIdx != lastIdx) {
                    PreMintData memory lastPreMintData = set.preMintDataArr[
                        lastIdx
                    ];
                    set.preMintDataArr[toDeleteIdx] = lastPreMintData;
                    set.indexes[lastPreMintData.sender] = idx;
                }
                set.preMintDataArr.pop();
                delete set.indexes[_sender];
            } else {
                set.preMintDataArr[idx - 1].quantity = newQuantity;
            }
        }
    }

    /// @dev Mint in batches of up to `_maxBatchSize`. Used to control
    /// gas costs for subsequent transfers in ERC721A contracts.
    /// @param _sender address to mint NFTs to
    /// @param _quantity No. of NFTs to mint
    /// @param _maxBatchSize Max no. of NFTs to mint in a batch
    function _batchMint(
        address _sender,
        uint256 _quantity,
        uint256 _maxBatchSize
    ) private {
        uint256 numChunks = _quantity / _maxBatchSize;
        for (uint256 i; i < numChunks; ++i) {
            _mint(_sender, _maxBatchSize, "", false);
        }
        uint256 remainingQty = _quantity % _maxBatchSize;
        if (remainingQty != 0) {
            _mint(_sender, remainingQty, "", false);
        }
    }

    /// @dev Update the address that the contract will make OperatorFilter checks against. When set to the zero
    /// address, checks will be bypassed.
    /// @param _newRegistry The address of the new OperatorFilterRegistry
    function _updateOperatorFilterRegistryAddress(
        IOperatorFilterRegistry _newRegistry
    ) private {
        operatorFilterRegistry = _newRegistry;
        emit OperatorFilterRegistryUpdated(_newRegistry);
    }

    /// @dev Checks if the address (the operator) trying to transfer the NFT is allowed
    /// @param operator Address of the operator
    function _checkFilterOperator(address operator) internal view virtual {
        IOperatorFilterRegistry registry = operatorFilterRegistry;
        // Check registry code length to facilitate testing in environments without a deployed registry.
        if (address(registry).code.length > 0) {
            if (!registry.isOperatorAllowed(address(this), operator)) {
                revert OperatorNotAllowed(operator);
            }
        }
    }

    /// @dev `setApprovalForAll` wrapper to prevent the sender to approve a non-allowed operator
    /// @param operator Address being approved
    /// @param approved Whether the operator is approved or not
    function setApprovalForAll(address operator, bool approved)
        public
        override(ERC721AUpgradeable, IERC721Upgradeable)
        onlyAllowedOperatorApproval(operator)
    {
        super.setApprovalForAll(operator, approved);
    }

    /// @dev `aprove` wrapper to prevent the sender to approve a non-allowed operator
    /// @param operator Address being approved
    /// @param tokenId TokenID approved
    function approve(address operator, uint256 tokenId)
        public
        override(ERC721AUpgradeable, IERC721Upgradeable)
        onlyAllowedOperatorApproval(operator)
    {
        super.approve(operator, tokenId);
    }

    /// @dev `transferFrom` wrapper to prevent a non-allowed operator to transfer the NFT
    /// @param from Address to transfer from
    /// @param to Address to transfer to
    /// @param tokenId TokenID to transfer
    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    )
        public
        override(ERC721AUpgradeable, IERC721Upgradeable)
        onlyAllowedOperator(from)
    {
        super.transferFrom(from, to, tokenId);
    }

    /// @dev `safeTransferFrom` wrapper to prevent a non-allowed operator to transfer the NFT
    /// @param from Address to transfer from
    /// @param to Address to transfer to
    /// @param tokenId TokenID to transfer
    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId
    )
        public
        override(ERC721AUpgradeable, IERC721Upgradeable)
        onlyAllowedOperator(from)
    {
        super.safeTransferFrom(from, to, tokenId);
    }

    /// @dev `safeTransferFrom` wrapper to prevent a non-allowed operator to transfer the NFT
    /// @param from Address to transfer from
    /// @param to Address to transfer to
    /// @param tokenId TokenID to transfer
    /// @param data Data to send along with a safe transfer check
    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        bytes memory data
    )
        public
        override(ERC721AUpgradeable, IERC721Upgradeable)
        onlyAllowedOperator(from)
    {
        super.safeTransferFrom(from, to, tokenId, data);
    }
}

contracts/BatchReveal.sol.content:
//SPDX-License-Identifier: CC0
pragma solidity ^0.8.4;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";

import "./chainlink/VRFConsumerBaseV2Upgradeable.sol";
import "./interfaces/IBatchReveal.sol";
import "./interfaces/IBaseLaunchpeg.sol";
import "./LaunchpegErrors.sol";

// Creator: Tubby Cats
/// https://github.com/tubby-cats/batch-nft-reveal

/// @title BatchReveal
/// @notice Implements a gas efficient way of revealing NFT URIs gradually
contract BatchReveal is
    IBatchReveal,
    VRFConsumerBaseV2Upgradeable,
    OwnableUpgradeable
{
    /// @notice Batch reveal configuration by launchpeg
    mapping(address => BatchRevealConfig) public override launchpegToConfig;

    /// @notice VRF request ids by launchpeg
    mapping(uint256 => address) public vrfRequestIdToLaunchpeg;

    /// @notice Randomized seeds used to shuffle TokenURIs by launchpeg
    mapping(address => mapping(uint256 => uint256))
        public
        override launchpegToBatchToSeed;

    /// @notice Last token that has been revealed by launchpeg
    mapping(address => uint256) public override launchpegToLastTokenReveal;

    /// @dev Size of the array that will store already taken URIs numbers by launchpeg
    mapping(address => uint256) public launchpegToRangeLength;

    /// @notice Contract uses VRF or pseudo-randomness
    bool public override useVRF;

    /// @notice Chainlink subscription ID
    uint64 public override subscriptionId;

    /// @notice The gas lane to use, which specifies the maximum gas price to bump to.
    /// For a list of available gas lanes on each network,
    /// see https://docs.chain.link/docs/vrf-contracts/#configurations
    bytes32 public override keyHash;

    /// @notice Depends on the number of requested values that you want sent to the
    /// fulfillRandomWords() function. Storing each word costs about 20,000 gas,
    /// so 100,000 is a safe default for this example contract. Test and adjust
    /// this limit based on the network that you select, the size of the request,
    /// and the processing of the callback request in the fulfillRandomWords()
    /// function.
    uint32 public override callbackGasLimit;

    /// @notice Number of block confirmations that the coordinator will wait before triggering the callback
    /// The default is 3
    uint16 public constant override requestConfirmations = 3;

    /// @notice Next batch that will be revealed by VRF (if activated) by launchpeg
    mapping(address => uint256) public override launchpegToNextBatchToReveal;

    /// @notice True when force revealed has been triggered for the given launchpeg
    /// @dev VRF will not be used anymore if a batch has been force revealed
    mapping(address => bool) public override launchpegToHasBeenForceRevealed;

    /// @notice Has the random number for a batch already been asked by launchpeg
    /// @dev Prevents people from spamming the random words request
    /// and therefore reveal more batches than expected
    mapping(address => mapping(uint256 => bool))
        public
        override launchpegToVrfRequestedForBatch;

    struct Range {
        int128 start;
        int128 end;
    }

    /// @dev Emitted on revealNextBatch() and forceReveal()
    /// @param baseLaunchpeg Base launchpeg address
    /// @param batchNumber The batch revealed
    /// @param batchSeed The random number drawn
    event Reveal(address baseLaunchpeg, uint256 batchNumber, uint256 batchSeed);

    /// @dev Emitted on setRevealBatchSize()
    /// @param baseLaunchpeg Base launchpeg address
    /// @param revealBatchSize New reveal batch size
    event RevealBatchSizeSet(address baseLaunchpeg, uint256 revealBatchSize);

    /// @dev Emitted on setRevealStartTime()
    /// @param baseLaunchpeg Base launchpeg address
    /// @param revealStartTime New reveal start time
    event RevealStartTimeSet(address baseLaunchpeg, uint256 revealStartTime);

    /// @dev Emitted on setRevealInterval()
    /// @param baseLaunchpeg Base launchpeg address
    /// @param revealInterval New reveal interval
    event RevealIntervalSet(address baseLaunchpeg, uint256 revealInterval);

    /// @dev emitted on setVRF()
    /// @param _vrfCoordinator Chainlink coordinator address
    /// @param _keyHash Keyhash of the gas lane wanted
    /// @param _subscriptionId Chainlink subscription ID
    /// @param _callbackGasLimit Max gas used by the coordinator callback
    event VRFSet(
        address _vrfCoordinator,
        bytes32 _keyHash,
        uint64 _subscriptionId,
        uint32 _callbackGasLimit
    );

    /// @dev Verify that batch reveal is configured for the given launchpeg
    modifier batchRevealInitialized(address _baseLaunchpeg) {
        if (!isBatchRevealInitialized(_baseLaunchpeg)) {
            revert Launchpeg__BatchRevealNotInitialized();
        }
        _;
    }

    /// @dev Verify that batch reveal hasn't started for the given launchpeg
    modifier revealNotStarted(address _baseLaunchpeg) {
        if (launchpegToLastTokenReveal[_baseLaunchpeg] != 0) {
            revert Launchpeg__BatchRevealStarted();
        }
        _;
    }

    /// @notice Initialize batch reveal
    function initialize() external override initializer {
        __Ownable_init();
    }

    /// @dev Configure batch reveal for a given launch
    /// @param _baseLaunchpeg Base launchpeg address
    /// @param _revealBatchSize Size of the batch reveal
    /// @param _revealStartTime Batch reveal start time
    /// @param _revealInterval Batch reveal interval
    function configure(
        address _baseLaunchpeg,
        uint256 _revealBatchSize,
        uint256 _revealStartTime,
        uint256 _revealInterval
    ) external override onlyOwner revealNotStarted(_baseLaunchpeg) {
        uint256 _collectionSize = IBaseLaunchpeg(_baseLaunchpeg)
            .collectionSize();
        launchpegToConfig[_baseLaunchpeg].collectionSize = _collectionSize;
        launchpegToConfig[_baseLaunchpeg].intCollectionSize = int128(
            int256(_collectionSize)
        );
        _setRevealBatchSize(_baseLaunchpeg, _revealBatchSize);
        _setRevealStartTime(_baseLaunchpeg, _revealStartTime);
        _setRevealInterval(_baseLaunchpeg, _revealInterval);
    }

    /// @notice Set the reveal batch size. Can only be set after
    /// batch reveal has been initialized and before a batch has
    /// been revealed.
    /// @param _baseLaunchpeg Base launchpeg address
    /// @param _revealBatchSize New reveal batch size
    function setRevealBatchSize(
        address _baseLaunchpeg,
        uint256 _revealBatchSize
    )
        public
        override
        onlyOwner
        batchRevealInitialized(_baseLaunchpeg)
        revealNotStarted(_baseLaunchpeg)
    {
        _setRevealBatchSize(_baseLaunchpeg, _revealBatchSize);
    }

    /// @notice Set the reveal batch size
    /// @param _baseLaunchpeg Base launchpeg address
    /// @param _revealBatchSize New reveal batch size
    function _setRevealBatchSize(
        address _baseLaunchpeg,
        uint256 _revealBatchSize
    ) internal {
        if (_revealBatchSize == 0) {
            revert Launchpeg__InvalidBatchRevealSize();
        }
        uint256 collectionSize = launchpegToConfig[_baseLaunchpeg]
            .collectionSize;
        if (
            collectionSize % _revealBatchSize != 0 ||
            _revealBatchSize > collectionSize
        ) {
            revert Launchpeg__InvalidBatchRevealSize();
        }
        launchpegToRangeLength[_baseLaunchpeg] =
            (collectionSize / _revealBatchSize) *
            2;
        launchpegToConfig[_baseLaunchpeg].revealBatchSize = _revealBatchSize;
        emit RevealBatchSizeSet(_baseLaunchpeg, _revealBatchSize);
    }

    /// @notice Set the batch reveal start time. Can only be set after
    /// batch reveal has been initialized and before a batch has
    /// been revealed.
    /// @param _baseLaunchpeg Base launchpeg address
    /// @param _revealStartTime New batch reveal start time
    function setRevealStartTime(
        address _baseLaunchpeg,
        uint256 _revealStartTime
    )
        public
        override
        onlyOwner
        batchRevealInitialized(_baseLaunchpeg)
        revealNotStarted(_baseLaunchpeg)
    {
        _setRevealStartTime(_baseLaunchpeg, _revealStartTime);
    }

    /// @notice Set the batch reveal start time.
    /// @param _baseLaunchpeg Base launchpeg address
    /// @param _revealStartTime New batch reveal start time
    function _setRevealStartTime(
        address _baseLaunchpeg,
        uint256 _revealStartTime
    ) internal {
        // probably a mistake if the reveal is more than 100 days in the future
        if (_revealStartTime > block.timestamp + 8_640_000) {
            revert Launchpeg__InvalidRevealDates();
        }
        launchpegToConfig[_baseLaunchpeg].revealStartTime = _revealStartTime;
        emit RevealStartTimeSet(_baseLaunchpeg, _revealStartTime);
    }

    /// @notice Set the batch reveal interval. Can only be set after
    /// batch reveal has been initialized and before a batch has
    /// been revealed.
    /// @param _baseLaunchpeg Base launchpeg address
    /// @param _revealInterval New batch reveal interval
    function setRevealInterval(address _baseLaunchpeg, uint256 _revealInterval)
        public
        override
        onlyOwner
        batchRevealInitialized(_baseLaunchpeg)
        revealNotStarted(_baseLaunchpeg)
    {
        _setRevealInterval(_baseLaunchpeg, _revealInterval);
    }

    /// @notice Set the batch reveal interval.
    /// @param _baseLaunchpeg Base launchpeg address
    /// @param _revealInterval New batch reveal interval
    function _setRevealInterval(address _baseLaunchpeg, uint256 _revealInterval)
        internal
    {
        // probably a mistake if reveal interval is longer than 10 days
        if (_revealInterval > 864_000) {
            revert Launchpeg__InvalidRevealDates();
        }
        launchpegToConfig[_baseLaunchpeg].revealInterval = _revealInterval;
        emit RevealIntervalSet(_baseLaunchpeg, _revealInterval);
    }

    /// @notice Set VRF configuration
    /// @param _vrfCoordinator Chainlink coordinator address
    /// @param _keyHash Keyhash of the gas lane wanted
    /// @param _subscriptionId Chainlink subscription ID
    /// @param _callbackGasLimit Max gas used by the coordinator callback
    function setVRF(
        address _vrfCoordinator,
        bytes32 _keyHash,
        uint64 _subscriptionId,
        uint32 _callbackGasLimit
    ) external override onlyOwner {
        if (_vrfCoordinator == address(0)) {
            revert Launchpeg__InvalidCoordinator();
        }

        (
            ,
            uint32 _maxGasLimit,
            bytes32[] memory s_provingKeyHashes
        ) = VRFCoordinatorV2Interface(_vrfCoordinator).getRequestConfig();

        // 20_000 is the cost of storing one word, callback cost will never be lower than that
        if (_callbackGasLimit > _maxGasLimit || _callbackGasLimit < 20_000) {
            revert Launchpeg__InvalidCallbackGasLimit();
        }

        bool keyHashFound;
        for (uint256 i; i < s_provingKeyHashes.length; i++) {
            if (s_provingKeyHashes[i] == _keyHash) {
                keyHashFound = true;
                break;
            }
        }

        if (!keyHashFound) {
            revert Launchpeg__InvalidKeyHash();
        }

        (, , , address[] memory consumers) = VRFCoordinatorV2Interface(
            _vrfCoordinator
        ).getSubscription(_subscriptionId);

        bool isInConsumerList;
        for (uint256 i; i < consumers.length; i++) {
            if (consumers[i] == address(this)) {
                isInConsumerList = true;
                break;
            }
        }

        if (!isInConsumerList) {
            revert Launchpeg__IsNotInTheConsumerList();
        }

        useVRF = true;
        setVRFConsumer(_vrfCoordinator);
        keyHash = _keyHash;
        subscriptionId = _subscriptionId;
        callbackGasLimit = _callbackGasLimit;

        emit VRFSet(
            _vrfCoordinator,
            _keyHash,
            _subscriptionId,
            _callbackGasLimit
        );
    }

    // Forked from openzeppelin
    /// @dev Returns the smallest of two numbers.
    /// @param _a First number to consider
    /// @param _b Second number to consider
    /// @return min Minimum between the two params
    function _min(int128 _a, int128 _b) internal pure returns (int128) {
        return _a < _b ? _a : _b;
    }

    /// @notice Fills the range array
    /// @dev Ranges include the start but not the end [start, end)
    /// @param _ranges initial range array
    /// @param _start beginning of the array to be added
    /// @param _end end of the array to be added
    /// @param _lastIndex last position in the range array to consider
    /// @param _intCollectionSize collection size
    /// @return newLastIndex new lastIndex to consider for the future range to be added
    function _addRange(
        Range[] memory _ranges,
        int128 _start,
        int128 _end,
        uint256 _lastIndex,
        int128 _intCollectionSize
    ) private view returns (uint256) {
        uint256 positionToAssume = _lastIndex;
        for (uint256 j; j < _lastIndex; j++) {
            int128 rangeStart = _ranges[j].start;
            int128 rangeEnd = _ranges[j].end;
            if (_start < rangeStart && positionToAssume == _lastIndex) {
                positionToAssume = j;
            }
            if (
                (_start < rangeStart && _end > rangeStart) ||
                (rangeStart <= _start && _end <= rangeEnd) ||
                (_start < rangeEnd && _end > rangeEnd)
            ) {
                int128 length = _end - _start;
                _start = _min(_start, rangeStart);
                _end = _start + length + (rangeEnd - rangeStart);
                _ranges[j] = Range(-1, -1); // Delete
            }
        }
        for (uint256 pos = _lastIndex; pos > positionToAssume; pos--) {
            _ranges[pos] = _ranges[pos - 1];
        }
        _ranges[positionToAssume] = Range(
            _start,
            _min(_end, _intCollectionSize)
        );
        _lastIndex++;
        if (_end > _intCollectionSize) {
            _addRange(
                _ranges,
                0,
                _end - _intCollectionSize,
                _lastIndex,
                _intCollectionSize
            );
            _lastIndex++;
        }
        return _lastIndex;
    }

    /// @dev Adds the last batch into the ranges array
    /// @param _baseLaunchpeg Base launchpeg address
    /// @param _lastBatch Batch number to consider
    /// @param _revealBatchSize Reveal batch size
    /// @param _intCollectionSize Collection size
    /// @param _rangeLength Range length
    /// @return ranges Ranges array filled with every URI taken by batches smaller or equal to lastBatch
    function _buildJumps(
        address _baseLaunchpeg,
        uint256 _lastBatch,
        uint256 _revealBatchSize,
        int128 _intCollectionSize,
        uint256 _rangeLength
    ) private view returns (Range[] memory) {
        Range[] memory ranges = new Range[](_rangeLength);
        uint256 lastIndex;
        for (uint256 i; i < _lastBatch; i++) {
            int128 start = int128(
                int256(
                    _getFreeTokenId(
                        _baseLaunchpeg,
                        launchpegToBatchToSeed[_baseLaunchpeg][i],
                        ranges,
                        _intCollectionSize
                    )
                )
            );
            int128 end = start + int128(int256(_revealBatchSize));
            lastIndex = _addRange(
                ranges,
                start,
                end,
                lastIndex,
                _intCollectionSize
            );
        }
        return ranges;
    }

    /// @dev Gets the random token URI number from tokenId
    /// @param _baseLaunchpeg Base launchpeg address
    /// @param _startId Token Id to consider
    /// @return uriId Revealed Token URI Id
    function getShuffledTokenId(address _baseLaunchpeg, uint256 _startId)
        external
        view
        override
        returns (uint256)
    {
        int128 intCollectionSize = launchpegToConfig[_baseLaunchpeg]
            .intCollectionSize;
        uint256 revealBatchSize = launchpegToConfig[_baseLaunchpeg]
            .revealBatchSize;
        uint256 batch = _startId / revealBatchSize;
        Range[] memory ranges = new Range[](
            launchpegToRangeLength[_baseLaunchpeg]
        );

        ranges = _buildJumps(
            _baseLaunchpeg,
            batch,
            revealBatchSize,
            intCollectionSize,
            launchpegToRangeLength[_baseLaunchpeg]
        );

        uint256 positionsToMove = (_startId % revealBatchSize) +
            launchpegToBatchToSeed[_baseLaunchpeg][batch];

        return
            _getFreeTokenId(
                _baseLaunchpeg,
                positionsToMove,
                ranges,
                intCollectionSize
            );
    }

    /// @dev Gets the shifted URI number from tokenId and range array
    /// @param _baseLaunchpeg Base launchpeg address
    /// @param _positionsToMoveStart Token URI offset if none of the URI Ids were taken
    /// @param _ranges Ranges array built by _buildJumps()
    /// @param _intCollectionSize Collection size
    /// @return uriId Revealed Token URI Id
    function _getFreeTokenId(
        address _baseLaunchpeg,
        uint256 _positionsToMoveStart,
        Range[] memory _ranges,
        int128 _intCollectionSize
    ) private view returns (uint256) {
        int128 positionsToMove = int128(int256(_positionsToMoveStart));
        int128 id;

        for (uint256 round = 0; round < 2; round++) {
            for (uint256 i; i < launchpegToRangeLength[_baseLaunchpeg]; i++) {
                int128 start = _ranges[i].start;
                int128 end = _ranges[i].end;
                if (id < start) {
                    int128 finalId = id + positionsToMove;
                    if (finalId < start) {
                        return uint256(uint128(finalId));
                    } else {
                        positionsToMove -= start - id;
                        id = end;
                    }
                } else if (id < end) {
                    id = end;
                }
            }
            if ((id + positionsToMove) >= _intCollectionSize) {
                positionsToMove -= _intCollectionSize - id;
                id = 0;
            }
        }
        return uint256(uint128(id + positionsToMove));
    }

    /// @dev Sets batch seed for specified batch number
    /// @param _baseLaunchpeg Base launchpeg address
    /// @param _batchNumber Batch number that needs to be revealed
    /// @param _collectionSize Collection size
    /// @param _revealBatchSize Reveal batch size
    function _setBatchSeed(
        address _baseLaunchpeg,
        uint256 _batchNumber,
        uint256 _collectionSize,
        uint256 _revealBatchSize
    ) internal {
        uint256 randomness = uint256(
            keccak256(
                abi.encode(
                    msg.sender,
                    tx.gasprice,
                    block.number,
                    block.timestamp,
                    block.difficulty,
                    blockhash(block.number - 1),
                    address(this)
                )
            )
        );

        // not perfectly random since the folding doesn't match bounds perfectly, but difference is small
        launchpegToBatchToSeed[_baseLaunchpeg][_batchNumber] =
            randomness %
            (_collectionSize - (_batchNumber * _revealBatchSize));
    }

    /// @dev Returns true if a batch can be revealed
    /// @param _baseLaunchpeg Base launchpeg address
    /// @param _totalSupply Number of token already minted
    /// @return hasToRevealInfo Returns a bool saying whether a reveal can be triggered or not
    /// and the number of the next batch that will be revealed
    function hasBatchToReveal(address _baseLaunchpeg, uint256 _totalSupply)
        public
        view
        override
        returns (bool, uint256)
    {
        uint256 revealBatchSize = launchpegToConfig[_baseLaunchpeg]
            .revealBatchSize;
        uint256 revealStartTime = launchpegToConfig[_baseLaunchpeg]
            .revealStartTime;
        uint256 revealInterval = launchpegToConfig[_baseLaunchpeg]
            .revealInterval;
        uint256 lastTokenRevealed = launchpegToLastTokenReveal[_baseLaunchpeg];
        uint256 batchNumber;
        unchecked {
            batchNumber = lastTokenRevealed / revealBatchSize;
        }

        // We don't want to reveal other batches if a VRF random words request is pending
        if (
            block.timestamp < revealStartTime + batchNumber * revealInterval ||
            _totalSupply < lastTokenRevealed + revealBatchSize ||
            launchpegToVrfRequestedForBatch[_baseLaunchpeg][batchNumber]
        ) {
            return (false, batchNumber);
        }

        return (true, batchNumber);
    }

    /// @dev Reveals next batch if possible
    /// @dev If using VRF, the reveal happens on the coordinator callback call
    /// @param _baseLaunchpeg Base launchpeg address
    /// @param _totalSupply Number of token already minted
    /// @return isRevealed Returns false if it is not possible to reveal the next batch
    function revealNextBatch(address _baseLaunchpeg, uint256 _totalSupply)
        external
        override
        returns (bool)
    {
        if (_baseLaunchpeg != msg.sender) {
            revert Launchpeg__Unauthorized();
        }

        uint256 batchNumber;
        bool canReveal;
        (canReveal, batchNumber) = hasBatchToReveal(
            _baseLaunchpeg,
            _totalSupply
        );

        if (!canReveal) {
            return false;
        }

        if (useVRF) {
            uint256 requestId = VRFCoordinatorV2Interface(vrfCoordinator)
                .requestRandomWords(
                    keyHash,
                    subscriptionId,
                    requestConfirmations,
                    callbackGasLimit,
                    1
                );
            vrfRequestIdToLaunchpeg[requestId] = _baseLaunchpeg;
            launchpegToVrfRequestedForBatch[_baseLaunchpeg][batchNumber] = true;
        } else {
            launchpegToLastTokenReveal[_baseLaunchpeg] += launchpegToConfig[
                _baseLaunchpeg
            ].revealBatchSize;
            _setBatchSeed(
                _baseLaunchpeg,
                batchNumber,
                launchpegToConfig[_baseLaunchpeg].collectionSize,
                launchpegToConfig[_baseLaunchpeg].revealBatchSize
            );
            emit Reveal(
                _baseLaunchpeg,
                batchNumber,
                launchpegToBatchToSeed[_baseLaunchpeg][batchNumber]
            );
        }

        return true;
    }

    /// @dev Callback triggered by the VRF coordinator
    /// @param _randomWords Array of random numbers provided by the VRF coordinator
    function fulfillRandomWords(
        uint256 _requestId,
        uint256[] memory _randomWords
    ) internal override {
        address baseLaunchpeg = vrfRequestIdToLaunchpeg[_requestId];

        if (launchpegToHasBeenForceRevealed[baseLaunchpeg]) {
            revert Launchpeg__HasBeenForceRevealed();
        }

        uint256 revealBatchSize = launchpegToConfig[baseLaunchpeg]
            .revealBatchSize;
        uint256 collectionSize = launchpegToConfig[baseLaunchpeg]
            .collectionSize;
        uint256 _batchToReveal = launchpegToNextBatchToReveal[baseLaunchpeg]++;
        uint256 _revealBatchSize = revealBatchSize;
        uint256 _seed = _randomWords[0] %
            (collectionSize - (_batchToReveal * _revealBatchSize));

        launchpegToBatchToSeed[baseLaunchpeg][_batchToReveal] = _seed;
        launchpegToLastTokenReveal[baseLaunchpeg] += _revealBatchSize;

        emit Reveal(
            baseLaunchpeg,
            _batchToReveal,
            launchpegToBatchToSeed[baseLaunchpeg][_batchToReveal]
        );
    }

    /// @dev Force reveal, should be restricted to owner
    function forceReveal(address _baseLaunchpeg) external override onlyOwner {
        uint256 revealBatchSize = launchpegToConfig[_baseLaunchpeg]
            .revealBatchSize;
        uint256 batchNumber;
        unchecked {
            batchNumber =
                launchpegToLastTokenReveal[_baseLaunchpeg] /
                revealBatchSize;
            launchpegToLastTokenReveal[_baseLaunchpeg] += revealBatchSize;
        }

        _setBatchSeed(
            _baseLaunchpeg,
            batchNumber,
            launchpegToConfig[_baseLaunchpeg].collectionSize,
            launchpegToConfig[_baseLaunchpeg].revealBatchSize
        );
        launchpegToHasBeenForceRevealed[_baseLaunchpeg] = true;
        emit Reveal(
            _baseLaunchpeg,
            batchNumber,
            launchpegToBatchToSeed[_baseLaunchpeg][batchNumber]
        );
    }

    /// @notice Returns true if batch reveal is configured for the given launchpeg
    /// Since the collection size is set only when batch reveal is initialized,
    /// and the collection size cannot be 0, we assume a 0 value means
    /// the batch reveal configuration has not been initialized.
    function isBatchRevealInitialized(address _baseLaunchpeg)
        public
        view
        override
        returns (bool)
    {
        return launchpegToConfig[_baseLaunchpeg].collectionSize != 0;
    }
}

contracts/chainlink/VRFConsumerBaseV2Upg.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

/// ------------------------------------------------------------------------------
/// @notice Interface for contracts using VRF randomness
///   Forked from chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol
/// ------------------------------------------------------------------------------

/// @dev PURPOSE
///
/// @dev Reggie the Random Oracle (not his real job) wants to provide randomness
/// @dev to Vera the verifier in such a way that Vera can be sure he's not
/// @dev making his output up to suit himself. Reggie provides Vera a public key
/// @dev to which he knows the secret key. Each time Vera provides a seed to
/// @dev Reggie, he gives back a value which is computed completely
/// @dev deterministically from the seed and the secret key.
///
/// @dev Reggie provides a proof by which Vera can verify that the output was
/// @dev correctly computed once Reggie tells it to her, but without that proof,
/// @dev the output is indistinguishable to her from a uniform random sample
/// @dev from the output space.
///
/// @dev The purpose of this contract is to make it easy for unrelated contracts
/// @dev to talk to Vera the verifier about the work Reggie is doing, to provide
/// @dev simple access to a verifiable source of randomness. It ensures 2 things:
/// @dev 1. The fulfillment came from the VRFCoordinator
/// @dev 2. The consumer contract implements fulfillRandomWords.
/// @dev USAGE
///
/// @dev Calling contracts must inherit from VRFConsumerBase, and can
/// @dev initialize VRFConsumerBase's attributes in their constructor as
/// @dev shown:
///
/// @dev   contract VRFConsumer {
/// @dev     constructor(<other arguments>, address _vrfCoordinator, address _link)
/// @dev       VRFConsumerBase(_vrfCoordinator) public {
/// @dev         <initialization with other arguments goes here>
/// @dev       }
/// @dev   }
///
/// @dev The oracle will have given you an ID for the VRF keypair they have
/// @dev committed to (let's call it keyHash). Create subscription, fund it
/// @dev and your consumer contract as a consumer of it (see VRFCoordinatorInterface
/// @dev subscription management functions).
/// @dev Call requestRandomWords(keyHash, subId, minimumRequestConfirmations,
/// @dev callbackGasLimit, numWords),
/// @dev see (VRFCoordinatorInterface for a description of the arguments).
///
/// @dev Once the VRFCoordinator has received and validated the oracle's response
/// @dev to your request, it will call your contract's fulfillRandomWords method.
///
/// @dev The randomness argument to fulfillRandomWords is a set of random words
/// @dev generated from your requestId and the blockHash of the request.
///
/// @dev If your contract could have concurrent requests open, you can use the
/// @dev requestId returned from requestRandomWords to track which response is associated
/// @dev with which randomness request.
/// @dev See "SECURITY CONSIDERATIONS" for principles to keep in mind,
/// @dev if your contract could have multiple requests in flight simultaneously.
///
/// @dev Colliding `requestId`s are cryptographically impossible as long as seeds
/// @dev differ.
///
/// ------------------------------------------------------------------------------
/// @dev SECURITY CONSIDERATIONS
///
/// @dev A method with the ability to call your fulfillRandomness method directly
/// @dev could spoof a VRF response with any random value, so it's critical that
/// @dev it cannot be directly called by anything other than this base contract
/// @dev (specifically, by the VRFConsumerBase.rawFulfillRandomness method).
///
/// @dev For your users to trust that your contract's random behavior is free
/// @dev from malicious interference, it's best if you can write it so that all
/// @dev behaviors implied by a VRF response are executed ///during/// your
/// @dev fulfillRandomness method. If your contract must store the response (or
/// @dev anything derived from it) and use it later, you must ensure that any
/// @dev user-significant behavior which depends on that stored value cannot be
/// @dev manipulated by a subsequent VRF request.
///
/// @dev Similarly, both miners and the VRF oracle itself have some influence
/// @dev over the order in which VRF responses appear on the blockchain, so if
/// @dev your contract could have multiple VRF requests in flight simultaneously,
/// @dev you must ensure that the order in which the VRF responses arrive cannot
/// @dev be used to manipulate your contract's user-significant behavior.
///
/// @dev Since the block hash of the block which contains the requestRandomness
/// @dev call is mixed into the input to the VRF ///last///, a sufficiently powerful
/// @dev miner could, in principle, fork the blockchain to evict the block
/// @dev containing the request, forcing the request to be included in a
/// @dev different block with a different hash, and therefore a different input
/// @dev to the VRF. However, such an attack would incur a substantial economic
/// @dev cost. This cost scales with the number of blocks the VRF oracle waits
/// @dev until it calls responds to a request. It is for this reason that
/// @dev that you can signal to an oracle you'd like them to wait longer before
/// @dev responding to the request (however this is not enforced in the contract
/// @dev and so remains effective only in the case of unmodified oracle software).
///

abstract contract VRFConsumerBaseV2Upgradeable {
    error OnlyCoordinatorCanFulfill(address have, address want);
    address internal vrfCoordinator;

    /// @notice Initializes the vrf coordinator address
    /// @param _vrfCoordinator address of VRFCoordinator contract
    function setVRFConsumer(address _vrfCoordinator) internal {
        vrfCoordinator = _vrfCoordinator;
    }

    /// @notice fulfillRandomness handles the VRF response. Your contract must
    /// @notice implement it. See "SECURITY CONSIDERATIONS" above for important
    /// @notice principles to keep in mind when implementing your fulfillRandomness
    /// @notice method.
    ///
    /// @dev VRFConsumerBaseV2 expects its subcontracts to have a method with this
    /// @dev signature, and will call it once it has verified the proof
    /// @dev associated with the randomness. (It is triggered via a call to
    /// @dev rawFulfillRandomness, below.)
    ///
    /// @param requestId The Id initially returned by requestRandomness
    /// @param randomWords the VRF output expanded to the requested number of words
    function fulfillRandomWords(uint256 requestId, uint256[] memory randomWords)
        internal
        virtual;

    /// @notice rawFulfillRandomness is called by VRFCoordinator when it receives a valid VRF
    /// proof. rawFulfillRandomness then calls fulfillRandomness, after validating
    /// the origin of the call
    function rawFulfillRandomWords(
        uint256 requestId,
        uint256[] memory randomWords
    ) external {
        if (msg.sender != vrfCoordinator) {
            revert OnlyCoordinatorCanFulfill(msg.sender, vrfCoordinator);
        }
        fulfillRandomWords(requestId, randomWords);
    }
}

contracts/ERC1155LaunchpegBase.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import {AccessControlEnumerableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/AccessControlEnumerableUpgradeable.sol";
import {ERC1155Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import {ERC2981Upgradeable} from "@openzeppelin/contracts-upgradeable/token/common/ERC2981Upgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import {StringsUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/StringsUpgradeable.sol";
import {IOperatorFilterRegistry} from "operator-filter-registry/src/IOperatorFilterRegistry.sol";

import "./LaunchpegErrors.sol";
import {SafePausableUpgradeable} from "./utils/SafePausableUpgradeable.sol";

import {IERC1155LaunchpegBase} from "./interfaces/IERC1155LaunchpegBase.sol";

abstract contract ERC1155LaunchpegBase is
    IERC1155LaunchpegBase,
    ERC1155Upgradeable,
    ERC2981Upgradeable,
    ReentrancyGuardUpgradeable,
    SafePausableUpgradeable
{
    using StringsUpgradeable for uint256;

    /// @notice Percentage base point
    uint256 private constant BASIS_POINT_PRECISION = 10_000;

    /// @notice Role granted to project owners
    bytes32 public constant PROJECT_OWNER_ROLE =
        keccak256("PROJECT_OWNER_ROLE");

    /// @notice Contract filtering allowed operators, preventing unauthorized contract to transfer NFTs
    /// By default, Launchpeg contracts are subscribed to OpenSea's Curated Subscription Address at 0x3cc6CddA760b79bAfa08dF41ECFA224f810dCeB6

    IOperatorFilterRegistry public operatorFilterRegistry;

    /// @notice The fees collected by Joepegs on the sale benefits
    /// @dev In basis points e.g 100 for 1%
    uint256 public joeFeePercent;

    /// @notice The address to which the fees on the sale will be sent
    address public joeFeeCollector;

    /// @notice Start time when funds can be withdrawn
    uint256 public withdrawAVAXStartTime;

    /// @notice This boolean can be turned on to prevent any changes on the sale parameters.
    /// @dev Once set to true, it shouldn't be possible to turn it back to false.
    bool public locked;

    string public name;

    string public symbol;

    struct InitData {
        address owner;
        address royaltyReceiver;
        uint256 joeFeePercent;
        string collectionName;
        string collectionSymbol;
    }

    enum Phase {
        NotStarted,
        DutchAuction,
        PreMint,
        Allowlist,
        PublicSale,
        Ended
    }

    /// @dev Emitted on updateOperatorFilterRegistryAddress()
    /// @param operatorFilterRegistry New operator filter registry
    event OperatorFilterRegistryUpdated(address operatorFilterRegistry);

    /// @dev Emitted on _setDefaultRoyalty()
    /// @param receiver Royalty fee collector
    /// @param feePercent Royalty fee percent in basis point
    event DefaultRoyaltySet(address indexed receiver, uint256 feePercent);

    /// @dev Emitted on setWithdrawAVAXStartTime()
    /// @param withdrawAVAXStartTime New withdraw AVAX start time
    event WithdrawAVAXStartTimeSet(uint256 withdrawAVAXStartTime);

    /// @dev Emitted on initializeJoeFee()
    /// @param feePercent The fees collected by Joepegs on the sale benefits
    /// @param feeCollector The address to which the fees on the sale will be sent
    event JoeFeeInitialized(uint256 feePercent, address feeCollector);

    /// @dev Emitted on withdrawAVAX()
    /// @param sender The address that withdrew the tokens
    /// @param amount Amount of AVAX transfered to `sender`
    /// @param fee Amount of AVAX paid to the fee collector
    event AvaxWithdraw(address indexed sender, uint256 amount, uint256 fee);

    /// @dev Emitted on setURI()
    /// @param uri The new base URI
    event URISet(string uri);

    event SaleParametersLocked();

    /// @notice Allow spending tokens from addresses with balance
    /// Note that this still allows listings and marketplaces with escrow to transfer tokens if transferred
    /// from an EOA.
    modifier onlyAllowedOperator(address from) virtual {
        if (from != msg.sender) {
            _checkFilterOperator(msg.sender);
        }
        _;
    }

    /// @notice Allow approving tokens transfers
    modifier onlyAllowedOperatorApproval(address operator) virtual {
        _checkFilterOperator(operator);
        _;
    }

    modifier contractNotLocked() {
        if (locked) {
            revert Launchpeg__SaleParametersLocked();
        }
        _;
    }

    /// @notice Checks if the current phase matches the required phase
    modifier atPhase(Phase phase) {
        if (currentPhase() != phase) {
            revert Launchpeg__WrongPhase();
        }
        _;
    }

    function __ERC1155LaunchpegBase_init(
        InitData calldata initData
    ) internal onlyInitializing {
        __ERC1155_init("");
        __ERC2981_init();
        __ReentrancyGuard_init();
        __SafeAccessControlEnumerable_init();

        // Default royalty is 5%
        _setDefaultRoyalty(initData.royaltyReceiver, 500);

        // Initialize the operator filter registry and subscribe to OpenSea's list
        IOperatorFilterRegistry _operatorFilterRegistry = IOperatorFilterRegistry(
                0x000000000000AAeB6D7670E522A718067333cd4E
            );

        if (address(_operatorFilterRegistry).code.length > 0) {
            _operatorFilterRegistry.registerAndSubscribe(
                address(this),
                0x3cc6CddA760b79bAfa08dF41ECFA224f810dCeB6
            );
        }

        _updateOperatorFilterRegistryAddress(_operatorFilterRegistry);

        name = initData.collectionName;
        symbol = initData.collectionSymbol;

        _initializeJoeFee(initData.joeFeePercent, initData.owner);

        grantRole(PROJECT_OWNER_ROLE, initData.royaltyReceiver);
        _transferOwnership(initData.owner);
    }

    /// @notice Returns the current phase
    /// @return phase Current phase
    function currentPhase() public view virtual returns (Phase);

    function uri(uint256 tokenId) public view override returns (string memory) {
        return string(abi.encodePacked(super.uri(tokenId), tokenId.toString()));
    }

    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        virtual
        override(
            SafePausableUpgradeable,
            ERC1155Upgradeable,
            ERC2981Upgradeable
        )
        returns (bool)
    {
        return
            interfaceId == type(IERC1155LaunchpegBase).interfaceId ||
            ERC1155Upgradeable.supportsInterface(interfaceId) ||
            ERC2981Upgradeable.supportsInterface(interfaceId) ||
            AccessControlEnumerableUpgradeable.supportsInterface(interfaceId);
    }

    /// @notice Set the base URI
    /// @dev This sets the URI for revealed tokens
    /// Only callable by project owner
    /// @param newURI Base URI to be set
    function setURI(string calldata newURI) external onlyOwner {
        _setURI(newURI);
        emit URISet(newURI);
    }

    /// @notice Set the withdraw AVAX start time.
    /// @param newWithdrawAVAXStartTime New public sale end time
    function setWithdrawAVAXStartTime(
        uint256 newWithdrawAVAXStartTime
    ) external onlyOwner {
        withdrawAVAXStartTime = newWithdrawAVAXStartTime;
        emit WithdrawAVAXStartTimeSet(newWithdrawAVAXStartTime);
    }

    function setRoyaltyInfo(
        address receiver,
        uint96 feePercent
    ) external onlyOwner {
        // Royalty fees are limited to 25%
        if (feePercent > 2_500) {
            revert Launchpeg__InvalidRoyaltyInfo();
        }
        _setDefaultRoyalty(receiver, feePercent);
        emit DefaultRoyaltySet(receiver, feePercent);
    }

    /// @notice Set the operator filter registry address
    /// @param newOperatorFilterRegistry New operator filter registry
    function setOperatorFilterRegistryAddress(
        address newOperatorFilterRegistry
    ) external onlyOwner {
        _updateOperatorFilterRegistryAddress(
            IOperatorFilterRegistry(newOperatorFilterRegistry)
        );
    }

    function lockSaleParameters() external onlyOwner {
        locked = true;

        emit SaleParametersLocked();
    }

    /// @notice Withdraw AVAX to the given recipient
    /// @param to Recipient of the earned AVAX
    function withdrawAVAX(
        address to
    ) external onlyOwnerOrRole(PROJECT_OWNER_ROLE) nonReentrant {
        if (
            block.timestamp < withdrawAVAXStartTime ||
            withdrawAVAXStartTime == 0
        ) {
            revert Launchpeg__WithdrawAVAXNotAvailable();
        }

        uint256 amount = address(this).balance;
        uint256 fee;
        bool sent;

        if (joeFeePercent > 0) {
            fee = (amount * joeFeePercent) / BASIS_POINT_PRECISION;
            amount = amount - fee;

            (sent, ) = joeFeeCollector.call{value: fee}("");
            if (!sent) {
                revert Launchpeg__TransferFailed();
            }
        }

        (sent, ) = to.call{value: amount}("");
        if (!sent) {
            revert Launchpeg__TransferFailed();
        }

        emit AvaxWithdraw(to, amount, fee);
    }

    function setApprovalForAll(
        address operator,
        bool approved
    ) public virtual override onlyAllowedOperatorApproval(operator) {
        super.setApprovalForAll(operator, approved);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) public override onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, id, amount, data);
    }

    function safeBatchTransferFrom(
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public virtual override onlyAllowedOperator(from) {
        super.safeBatchTransferFrom(from, to, ids, amounts, data);
    }

    /// @notice Initialize the sales fee percent taken by Joepegs and address that collects the fees
    /// @param newJoeFeePercent The fees collected by Joepegs on the sale benefits
    /// @param newJoeFeeCollector The address to which the fees on the sale will be sent
    function _initializeJoeFee(
        uint256 newJoeFeePercent,
        address newJoeFeeCollector
    ) internal {
        if (newJoeFeePercent > BASIS_POINT_PRECISION) {
            revert Launchpeg__InvalidPercent();
        }
        if (newJoeFeeCollector == address(0)) {
            revert Launchpeg__InvalidJoeFeeCollector();
        }
        joeFeePercent = newJoeFeePercent;
        joeFeeCollector = newJoeFeeCollector;
        emit JoeFeeInitialized(newJoeFeePercent, newJoeFeeCollector);
    }

    /**
     * @dev Update the address that the contract will make OperatorFilter checks against. When set to the zero
     * address, checks will be bypassed.
     * @param newRegistry The address of the new OperatorFilterRegistry
     */
    function _updateOperatorFilterRegistryAddress(
        IOperatorFilterRegistry newRegistry
    ) private {
        operatorFilterRegistry = newRegistry;
        emit OperatorFilterRegistryUpdated(address(newRegistry));
    }

    /// @dev Checks if the address (the operator) trying to transfer the NFT is allowed
    /// @param operator Address of the operator
    function _checkFilterOperator(address operator) internal view virtual {
        IOperatorFilterRegistry registry = operatorFilterRegistry;
        // Check registry code length to facilitate testing in environments without a deployed registry.
        if (address(registry).code.length > 0) {
            if (!registry.isOperatorAllowed(address(this), operator)) {
                revert OperatorNotAllowed(operator);
            }
        }
    }

    /// @dev Verifies that enough AVAX has been sent by the sender and refunds the extra tokens if any
    /// @param price The price paid by the sender for minting NFTs
    function _refundIfOver(uint256 price) internal {
        if (msg.value < price) {
            revert Launchpeg__NotEnoughAVAX(msg.value);
        }
        if (msg.value > price) {
            (bool success, ) = msg.sender.call{value: msg.value - price}("");
            if (!success) {
                revert Launchpeg__TransferFailed();
            }
        }
    }
}

contracts/ERC1155SingleBundle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "./LaunchpegErrors.sol";
import {ERC1155LaunchpegBase} from "./ERC1155LaunchpegBase.sol";
import {IERC1155LaunchpegSingleBundle} from "./interfaces/IERC1155LaunchpegSingleBundle.sol";

contract ERC1155SingleBundle is
    IERC1155LaunchpegSingleBundle,
    ERC1155LaunchpegBase
{
    struct PreMintData {
        address sender;
        uint96 quantity;
    }

    struct PreMintDataSet {
        PreMintData[] preMintDataArr;
        mapping(address => uint256) indexes;
    }

    uint256 public collectionSize;
    uint256 public maxPerAddressDuringMint;

    uint256 public preMintPrice;
    uint256 public publicSalePrice;

    uint256 public preMintStartTime;
    uint256 public publicSaleStartTime;
    uint256 public publicSaleEndTime;

    uint256 public amountForDevs;
    uint256 public amountForPreMint;

    uint256 public amountMintedByDevs;
    uint256 public amountMintedDuringPreMint;
    uint256 public amountClaimedDuringPreMint;
    uint256 public amountMintedDuringPublicSale;

    mapping(address => uint256) public allowlist;
    mapping(address => uint256) public numberMinted;

    uint256[] private _tokenSet;
    PreMintDataSet private _pendingPreMints;

    event AllowlistSeeded();
    event PreMintStartTimeSet(uint256 preMintStartTime);
    event PublicSaleStartTimeSet(uint256 publicSaleStartTime);
    event PublicSaleEndTimeSet(uint256 publicSaleEndTime);
    event AmountForDevsSet(uint256 amountForDevs);
    event AmountForPreMintSet(uint256 amountForPreMint);
    event PreMintPriceSet(uint256 preMintPrice);
    event PublicSalePriceSet(uint256 publicSalePrice);
    event MaxPerAddressDuringMintSet(uint256 maxPerAddressDuringMint);
    event CollectionSizeSet(uint256 collectionSize);
    event PhaseInitialized(
        uint256 preMintStartTime,
        uint256 publicSaleStartTime,
        uint256 publicSaleEndTime,
        uint256 preMintPrice,
        uint256 salePrice,
        uint256 withdrawAVAXStartTime
    );
    event DevMint(address indexed sender, uint256 quantity);
    event PreMint(address indexed sender, uint256 quantity, uint256 price);
    event TokenSetUpdated(uint256[] tokenSet);

    modifier isEOA() {
        if (tx.origin != msg.sender) {
            revert Launchpeg__Unauthorized();
        }
        _;
    }

    constructor() {
        _disableInitializers();
    }

    function initialize(
        InitData calldata initData,
        uint256 initialMaxSupply,
        uint256 initialAmountForDevs,
        uint256 initialAmountForPreMint,
        uint256 initialMaxPerAddressDuringMint,
        uint256[] calldata initialTokenSet
    ) external initializer {
        __ERC1155LaunchpegBase_init(initData);

        if (amountForDevs + amountForPreMint > collectionSize) {
            revert Launchpeg__LargerCollectionSizeNeeded();
        }

        collectionSize = initialMaxSupply;
        maxPerAddressDuringMint = initialMaxPerAddressDuringMint;

        amountForDevs = initialAmountForDevs;
        amountForPreMint = initialAmountForPreMint;
        _tokenSet = initialTokenSet;
    }

    function initializePhases(
        uint256 initialPreMintStartTime,
        uint256 initialPublicSaleStartTime,
        uint256 initialPublicSaleEndTime,
        uint256 initialPreMintPrice,
        uint256 initialPublicSalePrice
    ) external onlyOwner atPhase(Phase.NotStarted) {
        if (
            initialPreMintStartTime < block.timestamp ||
            initialPublicSaleStartTime < initialPreMintStartTime ||
            initialPublicSaleEndTime < initialPublicSaleStartTime
        ) {
            revert Launchpeg__InvalidPhases();
        }

        if (initialPreMintPrice > initialPublicSalePrice) {
            revert Launchpeg__InvalidAllowlistPrice();
        }

        preMintPrice = initialPreMintPrice;
        publicSalePrice = initialPublicSalePrice;
        preMintStartTime = initialPreMintStartTime;

        publicSaleStartTime = initialPublicSaleStartTime;
        publicSaleEndTime = initialPublicSaleEndTime;

        withdrawAVAXStartTime = initialPublicSaleStartTime + 3 days;

        emit PhaseInitialized(
            preMintStartTime,
            publicSaleStartTime,
            publicSaleEndTime,
            initialPreMintPrice,
            initialPublicSalePrice,
            initialPublicSaleStartTime + 3 days
        );
    }

    function tokenSet() external view returns (uint256[] memory) {
        return _tokenSet;
    }

    function currentPhase() public view override returns (Phase) {
        if (
            preMintStartTime == 0 ||
            publicSaleStartTime == 0 ||
            publicSaleEndTime == 0 ||
            block.timestamp < preMintStartTime
        ) {
            return Phase.NotStarted;
        } else if (
            amountMintedDuringPreMint + amountMintedDuringPublicSale ==
            collectionSize
        ) {
            return Phase.Ended;
        } else if (
            block.timestamp >= preMintStartTime &&
            block.timestamp < publicSaleStartTime
        ) {
            return Phase.PreMint;
        } else if (
            block.timestamp >= publicSaleStartTime &&
            block.timestamp < publicSaleEndTime
        ) {
            return Phase.PublicSale;
        }
        return Phase.Ended;
    }

    function amountOfUsersWaitingForPremintClaim()
        external
        view
        returns (uint256)
    {
        return _pendingPreMints.preMintDataArr.length;
    }

    function userPendingPreMints(address user) public view returns (uint256) {
        uint256 userIndex = _pendingPreMints.indexes[user];

        if (userIndex == 0) {
            return 0;
        }

        return _pendingPreMints.preMintDataArr[userIndex - 1].quantity;
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view override returns (bool) {
        return
            interfaceId == type(IERC1155LaunchpegSingleBundle).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function devMint(
        uint256 amount
    ) external whenNotPaused onlyOwnerOrRole(PROJECT_OWNER_ROLE) nonReentrant {
        uint256 amountAlreadyMinted = amountMintedByDevs;

        if (amountAlreadyMinted + amount > amountForDevs)
            revert Launchpeg__MaxSupplyForDevReached();

        amountMintedByDevs = amountAlreadyMinted + amount;

        _mint(msg.sender, amount);

        emit DevMint(msg.sender, amount);
    }

    function preMint(
        uint96 amount
    ) external payable whenNotPaused atPhase(Phase.PreMint) nonReentrant {
        if (amount == 0) {
            revert Launchpeg__InvalidQuantity();
        }

        uint256 userAllowlistAmount = allowlist[msg.sender];
        if (amount > userAllowlistAmount) {
            revert Launchpeg__NotEligibleForAllowlistMint();
        }

        uint256 amountAlreadyPreMinted = amountMintedDuringPreMint;
        if (amountAlreadyPreMinted + amount > amountForPreMint) {
            revert Launchpeg__MaxSupplyReached();
        }

        PreMintDataSet storage pmDataSet = _pendingPreMints;
        uint256 userIndex = pmDataSet.indexes[msg.sender];

        if (userIndex != 0) {
            pmDataSet.preMintDataArr[userIndex - 1].quantity += amount;
        } else {
            PreMintData memory preMintData = PreMintData({
                sender: msg.sender,
                quantity: amount
            });
            pmDataSet.preMintDataArr.push(preMintData);
            pmDataSet.indexes[msg.sender] = pmDataSet.preMintDataArr.length;
        }

        amountMintedDuringPreMint = amountAlreadyPreMinted + amount;
        allowlist[msg.sender] = userAllowlistAmount - amount;

        uint256 totalPrice = preMintPrice * amount;
        _refundIfOver(totalPrice);

        emit PreMint(msg.sender, amount, totalPrice);
    }

    function claimPremint() external whenNotPaused nonReentrant {
        if (block.timestamp < publicSaleStartTime) {
            revert Launchpeg__WrongPhase();
        }

        PreMintDataSet storage pmDataSet = _pendingPreMints;

        uint96 preMintQuantity;
        uint256 userIndex = pmDataSet.indexes[msg.sender];

        if (userIndex != 0)
            preMintQuantity = pmDataSet.preMintDataArr[userIndex - 1].quantity;

        if (preMintQuantity != 0) {
            uint256 lastIndex = pmDataSet.preMintDataArr.length - 1;
            if (lastIndex != userIndex - 1) {
                PreMintData memory lastPreMintData = pmDataSet.preMintDataArr[
                    lastIndex
                ];
                pmDataSet.preMintDataArr[userIndex - 1] = lastPreMintData;
                pmDataSet.indexes[lastPreMintData.sender] = userIndex;
            }
            pmDataSet.preMintDataArr.pop();
            delete pmDataSet.indexes[msg.sender];
        } else {
            revert Launchpeg__InvalidClaim();
        }

        amountClaimedDuringPreMint += preMintQuantity;

        _mint(msg.sender, preMintQuantity);
    }

    function batchClaimPreMint(
        uint256 numberOfClaims
    ) external whenNotPaused nonReentrant {
        if (block.timestamp < publicSaleStartTime) {
            revert Launchpeg__WrongPhase();
        }

        uint256 initialRemainingPreMints = _pendingPreMints
            .preMintDataArr
            .length;

        uint256 remainingPreMints = initialRemainingPreMints;
        uint256 tokenPreMinted = 0;

        while (remainingPreMints > 0 && numberOfClaims > 0) {
            PreMintData memory preMintData = _pendingPreMints.preMintDataArr[
                remainingPreMints - 1
            ];

            delete _pendingPreMints.indexes[preMintData.sender];

            tokenPreMinted += preMintData.quantity;
            remainingPreMints--;
            numberOfClaims--;

            _mint(preMintData.sender, preMintData.quantity);
        }

        amountClaimedDuringPreMint += tokenPreMinted;

        // Removing the pre-minted tokens from the array all at once
        PreMintData[] storage preMintDataArr = _pendingPreMints.preMintDataArr;
        assembly {
            sstore(preMintDataArr.slot, remainingPreMints)
        }
    }

    function publicSaleMint(
        uint256 amount
    )
        external
        payable
        whenNotPaused
        atPhase(Phase.PublicSale)
        nonReentrant
        isEOA
    {
        if (
            numberMinted[msg.sender] +
                userPendingPreMints(msg.sender) +
                amount >
            maxPerAddressDuringMint
        ) {
            revert Launchpeg__CanNotMintThisMany();
        }

        if (amount > _availableSupply()) {
            revert Launchpeg__MaxSupplyReached();
        }

        amountMintedDuringPublicSale += amount;

        _mint(msg.sender, amount);
        _refundIfOver(publicSalePrice * amount);
    }

    function updateTokenSet(uint256[] calldata newTokenSet) external onlyOwner {
        _tokenSet = newTokenSet;
        emit TokenSetUpdated(newTokenSet);
    }

    function seedAllowlist(
        address[] calldata addresses,
        uint256[] calldata amounts
    ) external onlyOwner {
        uint256 addressesLength = addresses.length;
        if (addressesLength != amounts.length) {
            revert Launchpeg__WrongAddressesAndNumSlotsLength();
        }
        for (uint256 i; i < addressesLength; i++) {
            allowlist[addresses[i]] = amounts[i];
        }

        emit AllowlistSeeded();
    }

    function setPreMintStartTime(
        uint256 newPreMintStartTime
    ) external onlyOwner contractNotLocked {
        if (newPreMintStartTime > publicSaleStartTime)
            revert Launchpeg__InvalidPhases();

        preMintStartTime = newPreMintStartTime;
        emit PreMintStartTimeSet(newPreMintStartTime);
    }

    function setPublicSaleStartTime(
        uint256 newPublicSaleStartTime
    ) external onlyOwner contractNotLocked {
        if (newPublicSaleStartTime > publicSaleEndTime)
            revert Launchpeg__InvalidPhases();

        publicSaleStartTime = newPublicSaleStartTime;
        emit PublicSaleStartTimeSet(newPublicSaleStartTime);
    }

    function setPublicSaleEndTime(
        uint256 newPublicSaleEndTime
    ) external onlyOwner contractNotLocked {
        if (newPublicSaleEndTime < publicSaleStartTime)
            revert Launchpeg__InvalidPhases();

        publicSaleEndTime = newPublicSaleEndTime;
        emit PublicSaleEndTimeSet(newPublicSaleEndTime);
    }

    function setAmountForDevs(
        uint256 newAmountForDevs
    ) external onlyOwner contractNotLocked {
        if (amountMintedByDevs > newAmountForDevs) {
            revert Launchpeg__MaxSupplyForDevReached();
        }

        amountForDevs = newAmountForDevs;
        emit AmountForDevsSet(newAmountForDevs);
    }

    function setAmountForPreMint(
        uint256 newAmountForPreMint
    ) external onlyOwner contractNotLocked {
        if (amountMintedDuringPreMint > newAmountForPreMint) {
            revert Launchpeg__MaxSupplyReached();
        }

        amountForPreMint = newAmountForPreMint;
        emit AmountForPreMintSet(newAmountForPreMint);
    }

    function setPreMintPrice(
        uint256 newPreMintPrice
    ) external onlyOwner contractNotLocked {
        if (newPreMintPrice > publicSalePrice)
            revert Launchpeg__InvalidAllowlistPrice();

        preMintPrice = newPreMintPrice;
        emit PreMintPriceSet(newPreMintPrice);
    }

    function setPublicSalePrice(
        uint256 newPublicSalePrice
    ) external onlyOwner contractNotLocked {
        if (newPublicSalePrice < preMintPrice)
            revert Launchpeg__InvalidAllowlistPrice();

        publicSalePrice = newPublicSalePrice;
        emit PublicSalePriceSet(newPublicSalePrice);
    }

    function setCollectionSize(
        uint256 newCollectionSize
    ) external onlyOwner contractNotLocked {
        if (
            newCollectionSize < amountForDevs + amountForPreMint ||
            newCollectionSize <
            amountMintedDuringPreMint +
                amountMintedDuringPublicSale +
                amountForDevs
        ) revert Launchpeg__LargerCollectionSizeNeeded();

        collectionSize = newCollectionSize;
        emit CollectionSizeSet(newCollectionSize);
    }

    function setMaxPerAddressDuringMint(
        uint256 newMaxAmountPerUser
    ) external onlyOwner contractNotLocked {
        maxPerAddressDuringMint = newMaxAmountPerUser;
        emit MaxPerAddressDuringMintSet(newMaxAmountPerUser);
    }

    function _availableSupply() internal view returns (uint256) {
        return
            collectionSize -
            amountMintedDuringPreMint -
            amountMintedDuringPublicSale -
            amountForDevs;
    }

    function _mint(address to, uint256 amount) internal {
        numberMinted[to] += amount;

        uint256 tokenAmount = _tokenSet.length;
        uint256[] memory amounts = new uint256[](tokenAmount);
        for (uint i = 0; i < tokenAmount; i++) {
            amounts[i] = amount;
        }

        _mintBatch(to, _tokenSet, amounts, "");
    }
}

contracts/ERC721AUpgradeable.sol.content:
// SPDX-License-Identifier: MIT
// Creator: Chiru Labs

pragma solidity ^0.8.4;

import "@openzeppelin/contracts-upgradeable/token/ERC721/IERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/IERC721ReceiverUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/IERC721MetadataUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/StringsUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/introspection/ERC165Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

error ApprovalCallerNotOwnerNorApproved();
error ApprovalQueryForNonexistentToken();
error ApproveToCaller();
error ApprovalToCurrentOwner();
error BalanceQueryForZeroAddress();
error MintToZeroAddress();
error MintZeroQuantity();
error OwnerQueryForNonexistentToken();
error TransferCallerNotOwnerNorApproved();
error TransferFromIncorrectOwner();
error TransferToNonERC721ReceiverImplementer();
error TransferToZeroAddress();
error URIQueryForNonexistentToken();

/**
 * @dev Implementation of https://eips.ethereum.org/EIPS/eip-721[ERC721] Non-Fungible Token Standard, including
 * the Metadata extension. Built to optimize for lower gas during batch mints.
 *
 * Assumes serials are sequentially minted starting at _startTokenId() (defaults to 0, e.g. 0, 1, 2, 3..).
 *
 * Assumes that an owner cannot have more than 2**64 - 1 (max value of uint64) of supply.
 *
 * Assumes that the maximum token id cannot exceed 2**256 - 1 (max value of uint256).
 */
contract ERC721AUpgradeable is
    Initializable,
    ContextUpgradeable,
    ERC165Upgradeable,
    IERC721Upgradeable,
    IERC721MetadataUpgradeable
{
    using AddressUpgradeable for address;
    using StringsUpgradeable for uint256;

    // Compiler will pack this into a single 256bit word.
    struct TokenOwnership {
        // The address of the owner.
        address addr;
        // Keeps track of the start time of ownership with minimal overhead for tokenomics.
        uint64 startTimestamp;
        // Whether the token has been burned.
        bool burned;
    }

    // Compiler will pack this into a single 256bit word.
    struct AddressData {
        // Realistically, 2**64-1 is more than enough.
        uint64 balance;
        // Keeps track of mint count with minimal overhead for tokenomics.
        uint64 numberMinted;
        // Keeps track of burn count with minimal overhead for tokenomics.
        uint64 numberBurned;
        // For miscellaneous variable(s) pertaining to the address
        // (e.g. number of whitelist mint slots used).
        // If there are multiple variables, please pack them into a uint64.
        uint64 aux;
    }

    // The tokenId of the next token to be minted.
    uint256 internal _currentIndex;

    // The number of tokens burned.
    uint256 internal _burnCounter;

    // Token name
    string private _name;

    // Token symbol
    string private _symbol;

    // Mapping from token ID to ownership details
    // An empty struct value does not necessarily mean the token is unowned. See _ownershipOf implementation for details.
    mapping(uint256 => TokenOwnership) internal _ownerships;

    // Mapping owner address to address data
    mapping(address => AddressData) private _addressData;

    // Mapping from token ID to approved address
    mapping(uint256 => address) private _tokenApprovals;

    // Mapping from owner to operator approvals
    mapping(address => mapping(address => bool)) private _operatorApprovals;

    function __ERC721A_init(string memory name_, string memory symbol_)
        internal
        onlyInitializing
    {
        __ERC721A_init_unchained(name_, symbol_);
    }

    function __ERC721A_init_unchained(
        string memory name_,
        string memory symbol_
    ) internal onlyInitializing {
        _name = name_;
        _symbol = symbol_;
        _currentIndex = _startTokenId();
    }

    /**
     * To change the starting tokenId, please override this function.
     */
    function _startTokenId() internal view virtual returns (uint256) {
        return 0;
    }

    /**
     * @dev Burned tokens are calculated here, use _totalMinted() if you want to count just minted tokens.
     */
    function totalSupply() public view returns (uint256) {
        // Counter underflow is impossible as _burnCounter cannot be incremented
        // more than _currentIndex - _startTokenId() times
        unchecked {
            return _currentIndex - _burnCounter - _startTokenId();
        }
    }

    /**
     * Returns the total amount of tokens minted in the contract.
     */
    function _totalMinted() internal view returns (uint256) {
        // Counter underflow is impossible as _currentIndex does not decrement,
        // and it is initialized to _startTokenId()
        unchecked {
            return _currentIndex - _startTokenId();
        }
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC165Upgradeable, IERC165Upgradeable)
        returns (bool)
    {
        return
            interfaceId == type(IERC721Upgradeable).interfaceId ||
            interfaceId == type(IERC721MetadataUpgradeable).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    /**
     * @dev See {IERC721-balanceOf}.
     */
    function balanceOf(address owner) public view override returns (uint256) {
        if (owner == address(0)) revert BalanceQueryForZeroAddress();
        return uint256(_addressData[owner].balance);
    }

    /**
     * Returns the number of tokens minted by `owner`.
     */
    function _numberMinted(address owner) internal view returns (uint256) {
        return uint256(_addressData[owner].numberMinted);
    }

    /**
     * Returns the number of tokens burned by or on behalf of `owner`.
     */
    function _numberBurned(address owner) internal view returns (uint256) {
        return uint256(_addressData[owner].numberBurned);
    }

    /**
     * Returns the auxillary data for `owner`. (e.g. number of whitelist mint slots used).
     */
    function _getAux(address owner) internal view returns (uint64) {
        return _addressData[owner].aux;
    }

    /**
     * Sets the auxillary data for `owner`. (e.g. number of whitelist mint slots used).
     * If there are multiple variables, please pack them into a uint64.
     */
    function _setAux(address owner, uint64 aux) internal {
        _addressData[owner].aux = aux;
    }

    /**
     * Gas spent here starts off proportional to the maximum mint batch size.
     * It gradually moves to O(1) as tokens get transferred around in the collection over time.
     */
    function _ownershipOf(uint256 tokenId)
        internal
        view
        returns (TokenOwnership memory)
    {
        uint256 curr = tokenId;

        unchecked {
            if (_startTokenId() <= curr && curr < _currentIndex) {
                TokenOwnership memory ownership = _ownerships[curr];
                if (!ownership.burned) {
                    if (ownership.addr != address(0)) {
                        return ownership;
                    }
                    // Invariant:
                    // There will always be an ownership that has an address and is not burned
                    // before an ownership that does not have an address and is not burned.
                    // Hence, curr will not underflow.
                    while (true) {
                        curr--;
                        ownership = _ownerships[curr];
                        if (ownership.addr != address(0)) {
                            return ownership;
                        }
                    }
                }
            }
        }
        revert OwnerQueryForNonexistentToken();
    }

    /**
     * @dev See {IERC721-ownerOf}.
     */
    function ownerOf(uint256 tokenId) public view override returns (address) {
        return _ownershipOf(tokenId).addr;
    }

    /**
     * @dev See {IERC721Metadata-name}.
     */
    function name() public view virtual override returns (string memory) {
        return _name;
    }

    /**
     * @dev See {IERC721Metadata-symbol}.
     */
    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

    /**
     * @dev See {IERC721Metadata-tokenURI}.
     */
    function tokenURI(uint256 tokenId)
        public
        view
        virtual
        override
        returns (string memory)
    {
        if (!_exists(tokenId)) revert URIQueryForNonexistentToken();
        string memory baseURI = _baseURI();
        return
            bytes(baseURI).length != 0
                ? string(abi.encodePacked(baseURI, tokenId.toString()))
                : "";
    }

    /**
     * @dev Base URI for computing {tokenURI}. If set, the resulting URI for each
     * token will be the concatenation of the `baseURI` and the `tokenId`. Empty
     * by default, can be overriden in child contracts.
     */
    function _baseURI() internal view virtual returns (string memory) {
        return "";
    }

    /**
     * @dev See {IERC721-approve}.
     */
    function approve(address to, uint256 tokenId) public virtual override {
        address owner = ERC721AUpgradeable.ownerOf(tokenId);
        if (to == owner) revert ApprovalToCurrentOwner();

        if (_msgSender() != owner && !isApprovedForAll(owner, _msgSender())) {
            revert ApprovalCallerNotOwnerNorApproved();
        }

        _approve(to, tokenId, owner);
    }

    /**
     * @dev See {IERC721-getApproved}.
     */
    function getApproved(uint256 tokenId)
        public
        view
        override
        returns (address)
    {
        if (!_exists(tokenId)) revert ApprovalQueryForNonexistentToken();

        return _tokenApprovals[tokenId];
    }

    /**
     * @dev See {IERC721-setApprovalForAll}.
     */
    function setApprovalForAll(address operator, bool approved)
        public
        virtual
        override
    {
        if (operator == _msgSender()) revert ApproveToCaller();

        _operatorApprovals[_msgSender()][operator] = approved;
        emit ApprovalForAll(_msgSender(), operator, approved);
    }

    /**
     * @dev See {IERC721-isApprovedForAll}.
     */
    function isApprovedForAll(address owner, address operator)
        public
        view
        virtual
        override
        returns (bool)
    {
        return _operatorApprovals[owner][operator];
    }

    /**
     * @dev See {IERC721-transferFrom}.
     */
    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public virtual override {
        _transfer(from, to, tokenId);
    }

    /**
     * @dev See {IERC721-safeTransferFrom}.
     */
    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public virtual override {
        safeTransferFrom(from, to, tokenId, "");
    }

    /**
     * @dev See {IERC721-safeTransferFrom}.
     */
    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        bytes memory _data
    ) public virtual override {
        _transfer(from, to, tokenId);
        if (
            to.isContract() &&
            !_checkContractOnERC721Received(from, to, tokenId, _data)
        ) {
            revert TransferToNonERC721ReceiverImplementer();
        }
    }

    /**
     * @dev Returns whether `tokenId` exists.
     *
     * Tokens can be managed by their owner or approved accounts via {approve} or {setApprovalForAll}.
     *
     * Tokens start existing when they are minted (`_mint`),
     */
    function _exists(uint256 tokenId) internal view returns (bool) {
        return
            _startTokenId() <= tokenId &&
            tokenId < _currentIndex &&
            !_ownerships[tokenId].burned;
    }

    function _safeMint(address to, uint256 quantity) internal {
        _safeMint(to, quantity, "");
    }

    /**
     * @dev Safely mints `quantity` tokens and transfers them to `to`.
     *
     * Requirements:
     *
     * - If `to` refers to a smart contract, it must implement {IERC721Receiver-onERC721Received}, which is called for each safe transfer.
     * - `quantity` must be greater than 0.
     *
     * Emits a {Transfer} event.
     */
    function _safeMint(
        address to,
        uint256 quantity,
        bytes memory _data
    ) internal {
        _mint(to, quantity, _data, true);
    }

    /**
     * @dev Mints `quantity` tokens and transfers them to `to`.
     *
     * Requirements:
     *
     * - `to` cannot be the zero address.
     * - `quantity` must be greater than 0.
     *
     * Emits a {Transfer} event.
     */
    function _mint(
        address to,
        uint256 quantity,
        bytes memory _data,
        bool safe
    ) internal {
        uint256 startTokenId = _currentIndex;
        if (to == address(0)) revert MintToZeroAddress();
        if (quantity == 0) revert MintZeroQuantity();

        _beforeTokenTransfers(address(0), to, startTokenId, quantity);

        // Overflows are incredibly unrealistic.
        // balance or numberMinted overflow if current value of either + quantity > 1.8e19 (2**64) - 1
        // updatedIndex overflows if _currentIndex + quantity > 1.2e77 (2**256) - 1
        unchecked {
            _addressData[to].balance += uint64(quantity);
            _addressData[to].numberMinted += uint64(quantity);

            _ownerships[startTokenId].addr = to;
            _ownerships[startTokenId].startTimestamp = uint64(block.timestamp);

            uint256 updatedIndex = startTokenId;
            uint256 end = updatedIndex + quantity;

            if (safe && to.isContract()) {
                do {
                    emit Transfer(address(0), to, updatedIndex);
                    if (
                        !_checkContractOnERC721Received(
                            address(0),
                            to,
                            updatedIndex++,
                            _data
                        )
                    ) {
                        revert TransferToNonERC721ReceiverImplementer();
                    }
                } while (updatedIndex != end);
                // Reentrancy protection
                if (_currentIndex != startTokenId) revert();
            } else {
                do {
                    emit Transfer(address(0), to, updatedIndex++);
                } while (updatedIndex != end);
            }
            _currentIndex = updatedIndex;
        }
        _afterTokenTransfers(address(0), to, startTokenId, quantity);
    }

    /**
     * @dev Transfers `tokenId` from `from` to `to`.
     *
     * Requirements:
     *
     * - `to` cannot be the zero address.
     * - `tokenId` token must be owned by `from`.
     *
     * Emits a {Transfer} event.
     */
    function _transfer(
        address from,
        address to,
        uint256 tokenId
    ) private {
        TokenOwnership memory prevOwnership = _ownershipOf(tokenId);

        if (prevOwnership.addr != from) revert TransferFromIncorrectOwner();

        bool isApprovedOrOwner = (_msgSender() == from ||
            isApprovedForAll(from, _msgSender()) ||
            getApproved(tokenId) == _msgSender());

        if (!isApprovedOrOwner) revert TransferCallerNotOwnerNorApproved();
        if (to == address(0)) revert TransferToZeroAddress();

        _beforeTokenTransfers(from, to, tokenId, 1);

        // Clear approvals from the previous owner
        _approve(address(0), tokenId, from);

        // Underflow of the sender's balance is impossible because we check for
        // ownership above and the recipient's balance can't realistically overflow.
        // Counter overflow is incredibly unrealistic as tokenId would have to be 2**256.
        unchecked {
            _addressData[from].balance -= 1;
            _addressData[to].balance += 1;

            TokenOwnership storage currSlot = _ownerships[tokenId];
            currSlot.addr = to;
            currSlot.startTimestamp = uint64(block.timestamp);

            // If the ownership slot of tokenId+1 is not explicitly set, that means the transfer initiator owns it.
            // Set the slot of tokenId+1 explicitly in storage to maintain correctness for ownerOf(tokenId+1) calls.
            uint256 nextTokenId = tokenId + 1;
            TokenOwnership storage nextSlot = _ownerships[nextTokenId];
            if (nextSlot.addr == address(0)) {
                // This will suffice for checking _exists(nextTokenId),
                // as a burned slot cannot contain the zero address.
                if (nextTokenId != _currentIndex) {
                    nextSlot.addr = from;
                    nextSlot.startTimestamp = prevOwnership.startTimestamp;
                }
            }
        }

        emit Transfer(from, to, tokenId);
        _afterTokenTransfers(from, to, tokenId, 1);
    }

    /**
     * @dev This is equivalent to _burn(tokenId, false)
     */
    function _burn(uint256 tokenId) internal virtual {
        _burn(tokenId, false);
    }

    /**
     * @dev Destroys `tokenId`.
     * The approval is cleared when the token is burned.
     *
     * Requirements:
     *
     * - `tokenId` must exist.
     *
     * Emits a {Transfer} event.
     */
    function _burn(uint256 tokenId, bool approvalCheck) internal virtual {
        TokenOwnership memory prevOwnership = _ownershipOf(tokenId);
        address from = prevOwnership.addr;
        if (approvalCheck) {
            bool isApprovedOrOwner = (_msgSender() == from ||
                isApprovedForAll(from, _msgSender()) ||
                getApproved(tokenId) == _msgSender());
            if (!isApprovedOrOwner) revert TransferCallerNotOwnerNorApproved();
        }
        _beforeTokenTransfers(from, address(0), tokenId, 1);
        // Clear approvals from the previous owner
        _approve(address(0), tokenId, from);
        // Underflow of the sender's balance is impossible because we check for
        // ownership above and the recipient's balance can't realistically overflow.
        // Counter overflow is incredibly unrealistic as tokenId would have to be 2**256.
        unchecked {
            AddressData storage addressData = _addressData[from];
            addressData.balance -= 1;
            addressData.numberBurned += 1;
            // Keep track of who burned the token, and the timestamp of burning.
            TokenOwnership storage currSlot = _ownerships[tokenId];
            currSlot.addr = from;
            currSlot.startTimestamp = uint64(block.timestamp);
            currSlot.burned = true;
            // If the ownership slot of tokenId+1 is not explicitly set, that means the burn initiator owns it.
            // Set the slot of tokenId+1 explicitly in storage to maintain correctness for ownerOf(tokenId+1) calls.
            uint256 nextTokenId = tokenId + 1;
            TokenOwnership storage nextSlot = _ownerships[nextTokenId];
            if (nextSlot.addr == address(0)) {
                // This will suffice for checking _exists(nextTokenId),
                // as a burned slot cannot contain the zero address.
                if (nextTokenId != _currentIndex) {
                    nextSlot.addr = from;
                    nextSlot.startTimestamp = prevOwnership.startTimestamp;
                }
            }
        }
        emit Transfer(from, address(0), tokenId);
        _afterTokenTransfers(from, address(0), tokenId, 1);
        // Overflow not possible, as _burnCounter cannot be exceed _currentIndex times.
        unchecked {
            _burnCounter++;
        }
    }

    /**
     * @dev Approve `to` to operate on `tokenId`
     *
     * Emits a {Approval} event.
     */
    function _approve(
        address to,
        uint256 tokenId,
        address owner
    ) private {
        _tokenApprovals[tokenId] = to;
        emit Approval(owner, to, tokenId);
    }

    /**
     * @dev Internal function to invoke {IERC721Receiver-onERC721Received} on a target contract.
     *
     * @param from address representing the previous owner of the given token ID
     * @param to target address that will receive the tokens
     * @param tokenId uint256 ID of the token to be transferred
     * @param _data bytes optional data to send along with the call
     * @return bool whether the call correctly returned the expected magic value
     */
    function _checkContractOnERC721Received(
        address from,
        address to,
        uint256 tokenId,
        bytes memory _data
    ) private returns (bool) {
        try
            IERC721ReceiverUpgradeable(to).onERC721Received(
                _msgSender(),
                from,
                tokenId,
                _data
            )
        returns (bytes4 retval) {
            return
                retval ==
                IERC721ReceiverUpgradeable(to).onERC721Received.selector;
        } catch (bytes memory reason) {
            if (reason.length == 0) {
                revert TransferToNonERC721ReceiverImplementer();
            } else {
                assembly {
                    revert(add(32, reason), mload(reason))
                }
            }
        }
    }

    /**
     * @dev Hook that is called before a set of serially-ordered token ids are about to be transferred. This includes minting.
     * And also called before burning one token.
     *
     * startTokenId - the first token id to be transferred
     * quantity - the amount to be transferred
     *
     * Calling conditions:
     *
     * - When `from` and `to` are both non-zero, `from`'s `tokenId` will be
     * transferred to `to`.
     * - When `from` is zero, `tokenId` will be minted for `to`.
     * - When `to` is zero, `tokenId` will be burned by `from`.
     * - `from` and `to` are never both zero.
     */
    function _beforeTokenTransfers(
        address from,
        address to,
        uint256 startTokenId,
        uint256 quantity
    ) internal virtual {}

    /**
     * @dev Hook that is called after a set of serially-ordered token ids have been transferred. This includes
     * minting.
     * And also called after one token has been burned.
     *
     * startTokenId - the first token id to be transferred
     * quantity - the amount to be transferred
     *
     * Calling conditions:
     *
     * - When `from` and `to` are both non-zero, `from`'s `tokenId` has been
     * transferred to `to`.
     * - When `from` is zero, `tokenId` has been minted for `to`.
     * - When `to` is zero, `tokenId` has been burned by `from`.
     * - `from` and `to` are never both zero.
     */
    function _afterTokenTransfers(
        address from,
        address to,
        uint256 startTokenId,
        uint256 quantity
    ) internal virtual {}

    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[42] private __gap;
}

contracts/FlatLaunchpeg.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "./interfaces/IFlatLaunchpeg.sol";
import "./BaseLaunchpeg.sol";

/// @title FlatLaunchpeg
/// @author Trader Joe
/// @notice Implements a simple minting NFT contract with an allowlist and public sale phase.
contract FlatLaunchpeg is BaseLaunchpeg, IFlatLaunchpeg {
    /// @notice Price of one NFT for people on the mint list
    /// @dev allowlistPrice is scaled to 1e18
    uint256 public override allowlistPrice;

    /// @notice Price of one NFT during the public sale
    /// @dev salePrice is scaled to 1e18
    uint256 public override salePrice;

    /// @dev Emitted on initializePhases()
    /// @param preMintStartTime Pre-mint start time in seconds
    /// @param allowlistStartTime Allowlist mint start time in seconds
    /// @param publicSaleStartTime Public sale start time in seconds
    /// @param publicSaleEndTime Public sale end time in seconds
    /// @param allowlistPrice Price of the allowlist sale in Avax
    /// @param salePrice Price of the public sale in Avax
    event PhaseInitialized(
        uint256 preMintStartTime,
        uint256 allowlistStartTime,
        uint256 publicSaleStartTime,
        uint256 publicSaleEndTime,
        uint256 allowlistPrice,
        uint256 salePrice
    );

    /// @notice FlatLaunchpeg initialization
    /// Can only be called once
    /// @param _collectionData Launchpeg collection data
    /// @param _ownerData Launchpeg owner data
    function initialize(
        CollectionData calldata _collectionData,
        CollectionOwnerData calldata _ownerData
    ) external override initializer {
        initializeBaseLaunchpeg(_collectionData, _ownerData);
    }

    /// @notice Initialize the two phases of the sale
    /// @dev Can only be called once
    /// @param _preMintStartTime Pre-mint start time in seconds
    /// @param _allowlistStartTime Allowlist mint start time in seconds
    /// @param _publicSaleStartTime Public sale start time in seconds
    /// @param _publicSaleEndTime Public sale end time in seconds
    /// @param _allowlistPrice Price of the allowlist sale in Avax
    /// @param _salePrice Price of the public sale in Avax
    function initializePhases(
        uint256 _preMintStartTime,
        uint256 _allowlistStartTime,
        uint256 _publicSaleStartTime,
        uint256 _publicSaleEndTime,
        uint256 _allowlistPrice,
        uint256 _salePrice
    ) external override onlyOwner atPhase(Phase.NotStarted) {
        if (
            _preMintStartTime < block.timestamp ||
            _allowlistStartTime < _preMintStartTime ||
            _publicSaleStartTime < _allowlistStartTime ||
            _publicSaleEndTime < _publicSaleStartTime
        ) {
            revert Launchpeg__InvalidPhases();
        }

        if (_allowlistPrice > _salePrice) {
            revert Launchpeg__InvalidAllowlistPrice();
        }

        salePrice = _salePrice;
        allowlistPrice = _allowlistPrice;

        preMintStartTime = _preMintStartTime;
        allowlistStartTime = _allowlistStartTime;
        publicSaleStartTime = _publicSaleStartTime;
        publicSaleEndTime = _publicSaleEndTime;

        emit PhaseInitialized(
            preMintStartTime,
            allowlistStartTime,
            publicSaleStartTime,
            publicSaleEndTime,
            allowlistPrice,
            salePrice
        );
    }

    /// @notice Set the pre-mint start time. Can only be set after phases
    /// have been initialized.
    /// @dev Only callable by owner
    /// @param _preMintStartTime New pre-mint start time
    function setPreMintStartTime(
        uint256 _preMintStartTime
    )
        external
        override
        onlyOwner
        isTimeUpdateAllowed(preMintStartTime)
        isNotBeforeBlockTimestamp(_preMintStartTime)
    {
        if (allowlistStartTime < _preMintStartTime) {
            revert Launchpeg__InvalidPhases();
        }
        preMintStartTime = _preMintStartTime;
        emit PreMintStartTimeSet(_preMintStartTime);
    }

    /// @notice Returns the current phase
    /// @return phase Current phase
    function currentPhase()
        public
        view
        override(IBaseLaunchpeg, BaseLaunchpeg)
        returns (Phase)
    {
        if (
            preMintStartTime == 0 ||
            allowlistStartTime == 0 ||
            publicSaleStartTime == 0 ||
            publicSaleEndTime == 0 ||
            block.timestamp < preMintStartTime
        ) {
            return Phase.NotStarted;
        } else if (totalSupply() >= collectionSize) {
            return Phase.Ended;
        } else if (
            block.timestamp >= preMintStartTime &&
            block.timestamp < allowlistStartTime
        ) {
            return Phase.PreMint;
        } else if (
            block.timestamp >= allowlistStartTime &&
            block.timestamp < publicSaleStartTime
        ) {
            return Phase.Allowlist;
        } else if (
            block.timestamp >= publicSaleStartTime &&
            block.timestamp < publicSaleEndTime
        ) {
            return Phase.PublicSale;
        }
        return Phase.Ended;
    }

    /// @dev Returns true if this contract implements the interface defined by
    /// `interfaceId`. See the corresponding
    /// https://eips.ethereum.org/EIPS/eip-165#how-interfaces-are-identified[EIP section]
    /// to learn more about how these IDs are created.
    /// This function call must use less than 30 000 gas.
    /// @param _interfaceId InterfaceId to consider. Comes from type(Interface).interfaceId
    /// @return isInterfaceSupported True if the considered interface is supported
    function supportsInterface(
        bytes4 _interfaceId
    )
        public
        view
        virtual
        override(BaseLaunchpeg, IERC165Upgradeable)
        returns (bool)
    {
        return
            _interfaceId == type(IFlatLaunchpeg).interfaceId ||
            super.supportsInterface(_interfaceId);
    }

    /// @dev Returns pre-mint price. Used by mint methods.
    function _getPreMintPrice() internal view override returns (uint256) {
        return allowlistPrice;
    }

    /// @dev Returns allowlist price. Used by mint methods.
    function _getAllowlistPrice() internal view override returns (uint256) {
        return allowlistPrice;
    }

    /// @dev Returns public sale price. Used by mint methods.
    function _getPublicSalePrice() internal view override returns (uint256) {
        return salePrice;
    }
}

contracts/interfaces/IBaseLaunchpeg.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts-upgradeable/token/ERC721/IERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/IERC721MetadataUpgradeable.sol";

import "../ERC721AUpgradeable.sol";

/// @title IBaseLaunchpeg
/// @author Trader Joe
/// @notice Defines the basic interface of BaseLaunchpeg
interface IBaseLaunchpeg is IERC721Upgradeable, IERC721MetadataUpgradeable {
    enum Phase {
        NotStarted,
        DutchAuction,
        PreMint,
        Allowlist,
        PublicSale,
        Ended
    }

    /// @notice Collection data to initialize Launchpeg
    /// @param name ERC721 name
    /// @param symbol ERC721 symbol
    /// @param maxPerAddressDuringMint Max amount of NFTs an address can mint in public phases
    /// @param collectionSize The collection size (e.g 10000)
    /// @param amountForDevs Amount of NFTs reserved for `projectOwner` (e.g 200)
    /// @param amountForAuction Amount of NFTs available for the auction (e.g 8000)
    /// @param amountForAllowlist Amount of NFTs available for the allowlist mint (e.g 1000)
    struct CollectionData {
        string name;
        string symbol;
        address batchReveal;
        uint256 maxPerAddressDuringMint;
        uint256 collectionSize;
        uint256 amountForDevs;
        uint256 amountForAuction;
        uint256 amountForAllowlist;
    }

    /// @notice Collection owner data to initialize Launchpeg
    /// @param owner The contract owner
    /// @param projectOwner The project owner
    /// @param royaltyReceiver Royalty fee collector
    /// @param joeFeeCollector The address to which the fees on the sale will be sent
    /// @param joeFeePercent The fees collected by the fee collector on the sale benefits
    struct CollectionOwnerData {
        address owner;
        address projectOwner;
        address royaltyReceiver;
        address joeFeeCollector;
        uint256 joeFeePercent;
    }

    function PROJECT_OWNER_ROLE() external pure returns (bytes32);

    function collectionSize() external view returns (uint256);

    function unrevealedURI() external view returns (string memory);

    function baseURI() external view returns (string memory);

    function amountForDevs() external view returns (uint256);

    function amountForAllowlist() external view returns (uint256);

    function maxPerAddressDuringMint() external view returns (uint256);

    function joeFeePercent() external view returns (uint256);

    function joeFeeCollector() external view returns (address);

    function allowlist(address) external view returns (uint256);

    function amountMintedByDevs() external view returns (uint256);

    function amountMintedDuringPreMint() external view returns (uint256);

    function amountClaimedDuringPreMint() external view returns (uint256);

    function amountMintedDuringAllowlist() external view returns (uint256);

    function amountMintedDuringPublicSale() external view returns (uint256);

    function preMintStartTime() external view returns (uint256);

    function allowlistStartTime() external view returns (uint256);

    function publicSaleStartTime() external view returns (uint256);

    function publicSaleEndTime() external view returns (uint256);

    function withdrawAVAXStartTime() external view returns (uint256);

    function allowlistPrice() external view returns (uint256);

    function salePrice() external view returns (uint256);

    function initializeBatchReveal(address _batchReveal) external;

    function setRoyaltyInfo(address receiver, uint96 feePercent) external;

    function seedAllowlist(
        address[] memory _addresses,
        uint256[] memory _numSlots
    ) external;

    function setBaseURI(string calldata baseURI) external;

    function setUnrevealedURI(string calldata baseURI) external;

    function setPreMintStartTime(uint256 _preMintStartTime) external;

    function setAllowlistStartTime(uint256 _allowlistStartTime) external;

    function setPublicSaleStartTime(uint256 _publicSaleStartTime) external;

    function setPublicSaleEndTime(uint256 _publicSaleEndTime) external;

    function setWithdrawAVAXStartTime(uint256 _withdrawAVAXStartTime) external;

    function devMint(uint256 quantity) external;

    function preMint(uint96 _quantity) external payable;

    function claimPreMint() external;

    function batchClaimPreMint(uint96 _maxQuantity) external;

    function allowlistMint(uint256 _quantity) external payable;

    function publicSaleMint(uint256 _quantity) external payable;

    function withdrawAVAX(address to) external;

    function getOwnershipData(uint256 tokenId)
        external
        view
        returns (ERC721AUpgradeable.TokenOwnership memory);

    function userPendingPreMints(address owner) external view returns (uint256);

    function numberMinted(address owner) external view returns (uint256);

    function numberMintedWithPreMint(address _owner)
        external
        view
        returns (uint256);

    function currentPhase() external view returns (Phase);

    function revealNextBatch() external;

    function hasBatchToReveal() external view returns (bool, uint256);
}

